// src/components/CoverLetterGeneration.js
import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../CSS/CoverLetterGeneration.css";

function CoverLetterGeneration() {
  const location = useLocation();
  const navigate = useNavigate();

  // Get the data passed from JDInput
  const { coverLetterData, companyName, jobDescription } = location.state || {};

  // State for editable content
  const [editableContent, setEditableContent] = useState({
    date: "",
    companyName: "",
    companyAddress: "",
    salutation: "Hiring Manager",
    content: "",
  });

  // State for PDF generation
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [pdfUrl, setPdfUrl] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  // Initialize data when component mounts
  useEffect(() => {
    if (coverLetterData) {
      // Parse the cover letter data to extract different parts
      const lines = coverLetterData.split("\n");

      // Try to extract date (usually the first line after header)
      const datePattern = /^[A-Z][a-z]+ \d{1,2}, \d{4}$/;
      let extractedDate = "";
      let extractedCompanyName = companyName || "";
      let extractedCompanyAddress = "";
      let extractedSalutation = "Hiring Manager";
      let contentStartIndex = 0;

      // Find the date
      for (let i = 0; i < lines.length; i++) {
        if (datePattern.test(lines[i].trim())) {
          extractedDate = lines[i].trim();

          // Company name is usually after date
          if (lines[i + 1] && lines[i + 1].includes("Hiring Manager")) {
            extractedSalutation = "Hiring Manager";
            contentStartIndex = i + 2;
          } else if (lines[i + 1]) {
            // Extract company info
            extractedCompanyName = lines[i + 1].trim() || companyName || "";
            extractedCompanyAddress = lines[i + 2] ? lines[i + 2].trim() : "";

            // Find salutation (usually starts with "Dear")
            for (let j = i + 1; j < lines.length; j++) {
              if (lines[j].startsWith("Dear ")) {
                extractedSalutation = lines[j]
                  .replace("Dear ", "")
                  .replace(",", "")
                  .trim();
                contentStartIndex = j + 1;
                break;
              }
            }
          }
          break;
        }
      }

      // Extract the main content (everything after salutation until signature)
      let mainContent = "";
      for (let i = contentStartIndex; i < lines.length; i++) {
        if (
          lines[i].includes("Warm regards") ||
          lines[i].includes("Sincerely") ||
          lines[i].includes("Best regards")
        ) {
          break;
        }
        if (lines[i].trim()) {
          mainContent += lines[i] + "\n\n";
        }
      }

      setEditableContent({
        date:
          extractedDate ||
          new Date().toLocaleDateString("en-US", {
            year: "numeric",
            month: "long",
            day: "numeric",
          }),
        companyName: extractedCompanyName,
        companyAddress: extractedCompanyAddress,
        salutation: extractedSalutation,
        content: mainContent.trim(),
      });

      setMessage(
        "✅ Cover letter loaded! You can edit the content before generating PDF."
      );
    } else {
      setMessage(
        "❌ No cover letter data found. Please generate content first."
      );
    }
  }, [coverLetterData, companyName]);

  // Handle content editing
  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setIsEditing(false);
    setMessage(
      "💾 Changes saved. Click 'Generate PDF' to create your cover letter."
    );
  };

  const handleFieldChange = (field, value) => {
    setEditableContent((prev) => ({ ...prev, [field]: value }));
  };

  // Generate PDF using Spring Boot backend
  const handleGeneratePDF = async () => {
    if (!editableContent.content) {
      setMessage("❌ Please add content before generating PDF");
      return;
    }

    setIsLoading(true);
    setMessage("⏳ Generating your cover letter PDF...");

    try {
      // Prepare data for backend
      const requestData = {
        COVER_LETTER_DATE: editableContent.date,
        COMPANY_NAME: editableContent.companyName,
        COMPANY_ADDRESS: editableContent.companyAddress,
        SALUTATION: editableContent.salutation,
        CONTENT: editableContent.content,
      };

      console.log("📤 Sending to backend:", requestData);

      const response = await axios.post(
        "http://localhost:8080/api/resume/generate-cover-letter",
        requestData,
        {
          responseType: "blob",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      // Create blob and URL for viewing
      const blob = new Blob([response.data], { type: "application/pdf" });
      const url = window.URL.createObjectURL(blob);

      setPdfUrl(url);
      setMessage("✅ Cover letter PDF generated successfully!");
    } catch (error) {
      console.error("❌ Error generating PDF:", error);
      setMessage(
        "❌ Error generating PDF. Make sure the backend is running on port 8080."
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Download PDF
  const handleDownloadPDF = () => {
    if (pdfUrl) {
      const link = document.createElement("a");
      link.href = pdfUrl;
      link.download = `cover-letter-${
        editableContent.companyName || "document"
      }.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      setMessage("📄 PDF downloaded successfully!");
    }
  };

  // Print PDF
  const handlePrintPDF = () => {
    if (pdfUrl) {
      const printWindow = window.open(pdfUrl, "_blank");
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  // Go back to JDInput
  const handleGoBack = () => {
    navigate("/jdinput");
  };

  // Copy to clipboard
  const handleCopy = () => {
    const fullContent = `${editableContent.date}

Hiring Manager
${editableContent.companyName}
${editableContent.companyAddress}

Dear ${editableContent.salutation},

${editableContent.content}

Warm regards,
Mahaboob Pasha Mohammad`;

    navigator.clipboard.writeText(fullContent);
    setMessage("📋 Cover letter copied to clipboard!");
    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div className="cover-letter-container">
      {/* Header */}
      <header className="cover-header">
        <button onClick={handleGoBack} className="back-btn">
          ← Back to Job Analysis
        </button>
        <div className="header-content">
          <h1>📄 Cover Letter Generation</h1>
          <p>Edit your cover letter and generate professional PDF</p>
        </div>
        {editableContent.companyName && (
          <span className="company-badge">
            🏢 {editableContent.companyName}
          </span>
        )}
      </header>

      {/* Three Column Layout */}
      <div className="three-column-layout">
        {/* Left Panel - Original Content */}
        <div className="left-panel">
          <div className="panel-header">
            <h2>📝 Original Content</h2>
            <button onClick={handleCopy} className="copy-btn">
              📋 Copy All
            </button>
          </div>
          <div className="original-content">
            <div className="content-section">
              <label>Date:</label>
              <p>{editableContent.date}</p>
            </div>
            <div className="content-section">
              <label>Company:</label>
              <p>{editableContent.companyName}</p>
            </div>
            <div className="content-section">
              <label>Address:</label>
              <p>{editableContent.companyAddress || "Not provided"}</p>
            </div>
            <div className="content-section">
              <label>Salutation:</label>
              <p>Dear {editableContent.salutation},</p>
            </div>
            <div className="content-section">
              <label>Content:</label>
              <p className="main-content">{editableContent.content}</p>
            </div>
          </div>
        </div>

        {/* Middle Panel - Editable Content */}
        <div className="middle-panel">
          <div className="panel-header">
            <h2>✏️ Edit Content</h2>
            <div className="edit-actions">
              {!isEditing ? (
                <button onClick={handleEdit} className="edit-btn">
                  ✏️ Edit All
                </button>
              ) : (
                <button onClick={handleSave} className="save-btn">
                  💾 Save Changes
                </button>
              )}
            </div>
          </div>

          <div className="editable-form">
            <div className="form-group">
              <label>Date:</label>
              <input
                type="text"
                value={editableContent.date}
                onChange={(e) => handleFieldChange("date", e.target.value)}
                disabled={!isEditing}
                placeholder="January 1, 2025"
              />
            </div>

            <div className="form-group">
              <label>Company Name:</label>
              <input
                type="text"
                value={editableContent.companyName}
                onChange={(e) =>
                  handleFieldChange("companyName", e.target.value)
                }
                disabled={!isEditing}
                placeholder="Company Name"
              />
            </div>

            <div className="form-group">
              <label>Company Address:</label>
              <input
                type="text"
                value={editableContent.companyAddress}
                onChange={(e) =>
                  handleFieldChange("companyAddress", e.target.value)
                }
                disabled={!isEditing}
                placeholder="123 Street, City, State ZIP"
              />
            </div>

            <div className="form-group">
              <label>Salutation (Dear ...):</label>
              <input
                type="text"
                value={editableContent.salutation}
                onChange={(e) =>
                  handleFieldChange("salutation", e.target.value)
                }
                disabled={!isEditing}
                placeholder="Hiring Manager"
              />
            </div>

            <div className="form-group">
              <label>Letter Content:</label>
              <textarea
                value={editableContent.content}
                onChange={(e) => handleFieldChange("content", e.target.value)}
                disabled={!isEditing}
                rows="15"
                placeholder="Enter your cover letter content..."
              />
            </div>

            <button
              onClick={handleGeneratePDF}
              disabled={!editableContent.content || isLoading}
              className="generate-pdf-btn"
            >
              {isLoading ? (
                <>
                  <span className="spinner-small"></span>
                  Generating PDF...
                </>
              ) : (
                "🎯 Generate Cover Letter PDF"
              )}
            </button>
          </div>
        </div>

        {/* Right Panel - PDF Preview */}
        <div className="right-panel">
          <div className="panel-header">
            <h2>📄 PDF Preview</h2>
            {pdfUrl && (
              <div className="pdf-actions">
                <button
                  onClick={handleDownloadPDF}
                  className="action-btn download"
                >
                  💾 Download
                </button>
                <button onClick={handlePrintPDF} className="action-btn print">
                  🖨️ Print
                </button>
              </div>
            )}
          </div>

          <div className="pdf-preview-container">
            {pdfUrl ? (
              <iframe
                src={pdfUrl}
                className="pdf-iframe"
                title="Cover Letter Preview"
              />
            ) : (
              <div className="no-preview">
                <div className="no-preview-content">
                  <span className="preview-icon">📄</span>
                  <h3>No Preview Available</h3>
                  <p>
                    Edit your content and click "Generate PDF" to see the
                    preview
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Status Message */}
      {message && (
        <div
          className={`status-message ${
            message.includes("✅")
              ? "success"
              : message.includes("❌")
              ? "error"
              : message.includes("⏳")
              ? "loading"
              : "info"
          }`}
        >
          {message}
        </div>
      )}
    </div>
  );
}

export default CoverLetterGeneration;
