export function sanitizeSkillsLatex(rawSkills) {
  if (!rawSkills) return "";

  console.log("üîß Raw skills input:\n", rawSkills);

  const cleanedLines = rawSkills
    .split("\n")
    .map((line, index) => {
      const parts = line.split("\\&");

      if (parts.length === 1) {
        console.log(`üî∏ Line ${index + 1} has no \&:`, line);
        return line.trim();
      }

      const first = parts[0].trim();
      const second = parts[1].trim();

      const rest = parts
        .slice(2)
        .map((part) => part.trim())
        .join(" & ");

      let cleaned = `${first} \\& ${second}`;
      if (rest) cleaned += ` & ${rest}`;
      if (!cleaned.trim().endsWith("\\\\")) cleaned += " \\\\";

      console.log(`üõ†Ô∏è  Line ${index + 1} sanitized:\n  ‚Üí ${cleaned}`);
      return cleaned;
    })
    .join("\n");

  console.log("‚úÖ Final sanitized skills:\n", cleanedLines);
  return cleanedLines;
}
