import React, { useState, useEffect } from "react";
import { sendCustomEmails } from "../services/coldEmailService";

const ColdEmailModal = ({ isOpen, onClose, coldEmail, companyName }) => {
  const [contacts, setContacts] = useState([]);
  const [newContact, setNewContact] = useState({
    name: "",
    email: "",
    title: "",
  });
  const [candidateInfo, setCandidateInfo] = useState({
    candidateName: "Mahaboob Pasha Mohammad",
    candidateEmail: "mahaboobpashamohamamd8@gmail.com",
    candidatePhone: "3143056056",
  });
  const [emailData, setEmailData] = useState({
    subject: "",
    body: "",
    companyName: companyName || "",
    hiringManagerName: "Hiring Manager",
  });
  const [attachments, setAttachments] = useState([]);
  const [sending, setSending] = useState(false);

  // Initialize email data when modal opens
  useEffect(() => {
    if (isOpen && coldEmail) {
      const emailSubject =
        coldEmail.match(/Subject:\s*(.*)/i)?.[1]?.trim() || "Job Application";
      const emailBody = coldEmail.replace(/Subject:.*\n?/i, "").trim();

      setEmailData({
        subject: emailSubject,
        body: emailBody,
        companyName: companyName || "",
        hiringManagerName: "Hiring Manager",
      });
    }
  }, [isOpen, coldEmail, companyName]);

  const addContact = () => {
    if (newContact.name && newContact.email) {
      const contact = {
        name: newContact.title
          ? `${newContact.name} (${newContact.title})`
          : newContact.name,
        email: newContact.email,
        company: emailData.companyName,
      };

      setContacts([...contacts, contact]);
      setNewContact({ name: "", email: "", title: "" });
    }
  };

  const removeContact = (index) => {
    setContacts(contacts.filter((_, i) => i !== index));
  };

  const handleFileAttachment = (event) => {
    const files = Array.from(event.target.files);
    const maxSize = 25 * 1024 * 1024; // 25MB limit
    const allowedTypes = [
      "application/pdf",
      "application/msword",
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
      "text/plain",
    ];

    const validFiles = files.filter((file) => {
      if (file.size > maxSize) {
        alert(`File "${file.name}" is too large. Maximum size is 25MB.`);
        return false;
      }
      if (!allowedTypes.includes(file.type)) {
        alert(
          `File "${file.name}" type not supported. Please use PDF, DOC, DOCX, or TXT files.`
        );
        return false;
      }
      return true;
    });

    // Convert files to base64 for sending
    validFiles.forEach((file) => {
      const reader = new FileReader();
      reader.onload = () => {
        const base64 = reader.result.split(",")[1]; // Remove data:type;base64, prefix
        setAttachments((prev) => [
          ...prev,
          {
            name: file.name,
            type: file.type,
            size: file.size,
            base64: base64,
          },
        ]);
      };
      reader.readAsDataURL(file);
    });
  };

  const removeAttachment = (index) => {
    setAttachments(attachments.filter((_, i) => i !== index));
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  const personalizeContent = (content) => {
    return content
      .replace(/\[CANDIDATE_NAME\]/g, candidateInfo.candidateName)
      .replace(/\[RECIPIENT_NAME\]/g, emailData.hiringManagerName)
      .replace(/\[COMPANY_NAME\]/g, emailData.companyName)
      .replace(/\[HIRING_MANAGER\]/g, emailData.hiringManagerName)
      .replace(/\{CANDIDATE_NAME\}/g, candidateInfo.candidateName)
      .replace(/\{RECIPIENT_NAME\}/g, emailData.hiringManagerName)
      .replace(/\{COMPANY_NAME\}/g, emailData.companyName)
      .replace(/\{HIRING_MANAGER\}/g, emailData.hiringManagerName);
  };

  const handleSendEmails = async () => {
    if (contacts.length === 0) {
      alert("Please add at least one HR contact.");
      return;
    }

    if (!emailData.companyName.trim()) {
      alert("Please enter the company name.");
      return;
    }

    setSending(true);
    try {
      const response = await sendCustomEmails({
        candidateName: candidateInfo.candidateName,
        candidateEmail: candidateInfo.candidateEmail,
        candidatePhone: candidateInfo.candidatePhone,
        companyName: emailData.companyName,
        customSubject: personalizeContent(emailData.subject),
        customBody: personalizeContent(emailData.body),
        selectedContacts: contacts,
        attachments: attachments, // Include attachments
      });

      if (response.data.status === "success") {
        alert(
          `‚úÖ Success! ${response.data.successCount} emails sent successfully out of ${response.data.totalEmails} total emails!`
        );
        onClose();
        setContacts([]);
        setAttachments([]);
      } else {
        alert(
          `‚ùå ${
            response.data.message ||
            "Some emails failed to send. Check console for details."
          }`
        );
      }
    } catch (error) {
      console.error("Send error:", error);

      if (error.response) {
        alert(
          `‚ùå Backend error: ${error.response.data.message || "Unknown error"}`
        );
      } else if (error.request) {
        alert(
          "‚ùå Cannot connect to backend. Make sure Spring Boot is running on port 8080."
        );
      } else {
        alert(`‚ùå Error: ${error.message}`);
      }
    } finally {
      setSending(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
      }}
    >
      <div
        style={{
          backgroundColor: "white",
          width: "95%",
          maxWidth: "800px",
          maxHeight: "90vh",
          borderRadius: "10px",
          padding: "20px",
          overflow: "auto",
        }}
      >
        {/* Header */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "20px",
            borderBottom: "1px solid #eee",
            paddingBottom: "10px",
          }}
        >
          <h2 style={{ margin: 0 }}>üöÄ Send Cold Email</h2>
          <button
            onClick={onClose}
            style={{
              background: "none",
              border: "none",
              fontSize: "24px",
              cursor: "pointer",
            }}
          >
            √ó
          </button>
        </div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: "20px",
          }}
        >
          {/* Left Column */}
          <div>
            {/* Candidate Info */}
            <div style={{ marginBottom: "20px" }}>
              <h3>Your Information</h3>
              <div style={{ display: "grid", gap: "10px" }}>
                <input
                  type="text"
                  placeholder="Your Name"
                  value={candidateInfo.candidateName}
                  onChange={(e) =>
                    setCandidateInfo({
                      ...candidateInfo,
                      candidateName: e.target.value,
                    })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
                <input
                  type="email"
                  placeholder="Your Email"
                  value={candidateInfo.candidateEmail}
                  onChange={(e) =>
                    setCandidateInfo({
                      ...candidateInfo,
                      candidateEmail: e.target.value,
                    })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
                <input
                  type="tel"
                  placeholder="Your Phone"
                  value={candidateInfo.candidatePhone}
                  onChange={(e) =>
                    setCandidateInfo({
                      ...candidateInfo,
                      candidatePhone: e.target.value,
                    })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
              </div>
            </div>

            {/* Company Info */}
            <div style={{ marginBottom: "20px" }}>
              <h3>Company Information</h3>
              <div style={{ display: "grid", gap: "10px" }}>
                <input
                  type="text"
                  placeholder="Company Name"
                  value={emailData.companyName}
                  onChange={(e) =>
                    setEmailData({ ...emailData, companyName: e.target.value })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
                <input
                  type="text"
                  placeholder="Hiring Manager Name"
                  value={emailData.hiringManagerName}
                  onChange={(e) =>
                    setEmailData({
                      ...emailData,
                      hiringManagerName: e.target.value,
                    })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
              </div>
            </div>

            {/* Add HR Contacts */}
            <div style={{ marginBottom: "20px" }}>
              <h3>Add HR Contacts</h3>
              <div
                style={{ display: "grid", gap: "10px", marginBottom: "10px" }}
              >
                <input
                  type="text"
                  placeholder="HR Name"
                  value={newContact.name}
                  onChange={(e) =>
                    setNewContact({ ...newContact, name: e.target.value })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
                <input
                  type="email"
                  placeholder="HR Email"
                  value={newContact.email}
                  onChange={(e) =>
                    setNewContact({ ...newContact, email: e.target.value })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
                <input
                  type="text"
                  placeholder="Job Title (optional)"
                  value={newContact.title}
                  onChange={(e) =>
                    setNewContact({ ...newContact, title: e.target.value })
                  }
                  style={{
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
              </div>
              <button
                onClick={addContact}
                disabled={!newContact.name || !newContact.email}
                style={{
                  padding: "8px 16px",
                  backgroundColor: "#10b981",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Add Contact
              </button>
            </div>

            {/* Contact List */}
            {contacts.length > 0 && (
              <div style={{ marginBottom: "20px" }}>
                <h4>HR Contacts ({contacts.length})</h4>
                <div style={{ maxHeight: "150px", overflow: "auto" }}>
                  {contacts.map((contact, index) => (
                    <div
                      key={index}
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                        padding: "8px",
                        backgroundColor: "#f9fafb",
                        marginBottom: "5px",
                        borderRadius: "4px",
                      }}
                    >
                      <div>
                        <div style={{ fontWeight: "500", fontSize: "14px" }}>
                          {contact.name}
                        </div>
                        <div style={{ fontSize: "12px", color: "#666" }}>
                          {contact.email}
                        </div>
                      </div>
                      <button
                        onClick={() => removeContact(index)}
                        style={{
                          background: "none",
                          border: "none",
                          color: "#ef4444",
                          cursor: "pointer",
                        }}
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Right Column */}
          <div>
            {/* Email Editor */}
            <div style={{ marginBottom: "20px" }}>
              <h3>Email Content</h3>
              <div style={{ marginBottom: "10px" }}>
                <label
                  style={{
                    display: "block",
                    marginBottom: "5px",
                    fontWeight: "500",
                  }}
                >
                  Subject:
                </label>
                <input
                  type="text"
                  value={emailData.subject}
                  onChange={(e) =>
                    setEmailData({ ...emailData, subject: e.target.value })
                  }
                  style={{
                    width: "100%",
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
              </div>
              <div>
                <label
                  style={{
                    display: "block",
                    marginBottom: "5px",
                    fontWeight: "500",
                  }}
                >
                  Body:
                </label>
                <textarea
                  value={emailData.body}
                  onChange={(e) =>
                    setEmailData({ ...emailData, body: e.target.value })
                  }
                  rows="12"
                  style={{
                    width: "100%",
                    padding: "8px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                    resize: "vertical",
                    fontFamily: "monospace",
                    fontSize: "13px",
                  }}
                />
              </div>
              <div
                style={{
                  fontSize: "12px",
                  color: "#666",
                  marginTop: "5px",
                  padding: "8px",
                  backgroundColor: "#f0f9ff",
                  borderRadius: "4px",
                }}
              >
                üí° Use placeholders: [CANDIDATE_NAME], [RECIPIENT_NAME],
                [COMPANY_NAME], [HIRING_MANAGER]
              </div>
            </div>

            {/* File Attachments */}
            <div style={{ marginBottom: "20px" }}>
              <h3>Attachments</h3>
              <input
                type="file"
                multiple
                accept=".pdf,.doc,.docx,.txt"
                onChange={handleFileAttachment}
                style={{ marginBottom: "10px" }}
              />
              <div
                style={{
                  fontSize: "12px",
                  color: "#666",
                  marginBottom: "10px",
                }}
              >
                Supported: PDF, DOC, DOCX, TXT (Max 25MB each)
              </div>

              {attachments.length > 0 && (
                <div>
                  <h4>Attached Files ({attachments.length})</h4>
                  {attachments.map((file, index) => (
                    <div
                      key={index}
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                        padding: "8px",
                        backgroundColor: "#f9fafb",
                        marginBottom: "5px",
                        borderRadius: "4px",
                      }}
                    >
                      <div>
                        <div style={{ fontWeight: "500", fontSize: "14px" }}>
                          {file.name}
                        </div>
                        <div style={{ fontSize: "12px", color: "#666" }}>
                          {formatFileSize(file.size)}
                        </div>
                      </div>
                      <button
                        onClick={() => removeAttachment(index)}
                        style={{
                          background: "none",
                          border: "none",
                          color: "#ef4444",
                          cursor: "pointer",
                        }}
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Preview */}
            <div style={{ marginBottom: "20px" }}>
              <h3>Preview</h3>
              <div
                style={{
                  border: "1px solid #ddd",
                  borderRadius: "4px",
                  padding: "10px",
                  backgroundColor: "#f9fafb",
                  fontSize: "13px",
                }}
              >
                <div style={{ marginBottom: "8px" }}>
                  <strong>Subject:</strong>{" "}
                  {personalizeContent(emailData.subject)}
                </div>
                <div
                  style={{
                    whiteSpace: "pre-line",
                    maxHeight: "100px",
                    overflow: "auto",
                  }}
                >
                  {personalizeContent(emailData.body)}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Send Button */}
        <div
          style={{
            borderTop: "1px solid #eee",
            paddingTop: "15px",
            display: "flex",
            justifyContent: "flex-end",
            gap: "10px",
          }}
        >
          <button
            onClick={onClose}
            style={{
              padding: "10px 20px",
              backgroundColor: "#6b7280",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Cancel
          </button>
          <button
            onClick={handleSendEmails}
            disabled={
              sending || contacts.length === 0 || !emailData.companyName.trim()
            }
            style={{
              padding: "10px 20px",
              backgroundColor:
                contacts.length > 0 && emailData.companyName.trim()
                  ? "#3b82f6"
                  : "#9ca3af",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor:
                contacts.length > 0 && emailData.companyName.trim()
                  ? "pointer"
                  : "not-allowed",
            }}
          >
            {sending ? "Sending..." : `Send to ${contacts.length} Contact(s)`}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ColdEmailModal;
