import React, { useState, useEffect } from "react";
import "../CSS/JDInput.css";

function JDInput() {
  const [jobDescription, setJobDescription] = useState("");
  const [jobCount, setJobCount] = useState(1);
  const [companyName, setCompanyName] = useState("Your Company Name");
  const [editableCompany, setEditableCompany] = useState(false);
  const [resumeOutput, setResumeOutput] = useState("");
  const [coverLetter, setCoverLetter] = useState("");
  const [whyIFit, setWhyIFit] = useState("");

  // Load job counter from local storage
  useEffect(() => {
    const count = localStorage.getItem("jobCounter");
    if (count) setJobCount(parseInt(count, 10));
  }, []);

  const handleNextJob = () => {
    const nextCount = jobCount + 1;
    setJobCount(nextCount);
    localStorage.setItem("jobCounter", nextCount);
    window.location.reload();
  };

  const handleJDChange = (e) => {
    setJobDescription(e.target.value);
  };

  const toggleCompanyEdit = () => {
    setEditableCompany(true);
  };

  const saveCompanyName = () => {
    setEditableCompany(false);
  };

  return (
    <div className="jdinput-container">
      {/* LEFT PANEL */}
      <div className="left-panel">
        <h2>Job Description</h2>
        <textarea
          placeholder="Paste job description here..."
          value={jobDescription}
          onChange={handleJDChange}
        />

        <div className="action-buttons">
          <button className="generate-btn">Generate Resume</button>
          <button className="generate-btn">Generate Cover Letter</button>
          <button className="generate-btn">Generate Why I Fit</button>
        </div>
      </div>

      {/* CENTER PANEL */}
      <div className="center-panel">
        <div className="job-info-bar">
          <h2>üßæ Job #{jobCount}</h2>
          <button className="job-btn" onClick={handleNextJob}>
            ‚ûï Next Job
          </button>
          <button
            className="job-btn reset"
            onClick={() => {
              localStorage.removeItem("jobCounter");
              window.location.reload();
            }}
          >
            üîÅ Reset Counter
          </button>
        </div>

        <div className="company-name-edit">
          {editableCompany ? (
            <>
              <input
                className="company-input"
                value={companyName}
                onChange={(e) => setCompanyName(e.target.value)}
              />
              <button className="save-btn" onClick={saveCompanyName}>
                üíæ Save
              </button>
            </>
          ) : (
            <>
              <h2 className="company-name" onClick={toggleCompanyEdit}>
                üè¢ {companyName} <span className="edit-hint">(edit)</span>
              </h2>
            </>
          )}
        </div>

        <div className="output-flow">
          <p>üìÑ Resume ‚ûù üìù Cover Letter ‚ûù üìå Why I Fit</p>
        </div>
      </div>

      {/* RIGHT PANEL */}
      <div className="right-panel">
        <button className="gmailManagerButton">üì¨ Gmail Manager</button>

        <div className="output-card">
          <h3>üìÑ Final Resume</h3>
          <textarea value={resumeOutput} readOnly />
          <button className="copy-btn">Copy</button>
        </div>

        <div className="output-card">
          <h3>üìù Cover Letter</h3>
          <textarea value={coverLetter} readOnly />
          <button className="copy-btn">Copy</button>
        </div>

        <div className="output-card">
          <h3>üìå Why I Fit</h3>
          <textarea value={whyIFit} readOnly />
          <button className="copy-btn">Copy</button>
        </div>
      </div>
    </div>
  );
}

export default JDInput;
