// App.js
import React, { useState } from "react";
import JDInput from "./components/JDInput";
import CoverLetter from "./components/CoverLetter";
import ColdEmail from "./components/ColdEmail";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState("resume");
  const [jobDescription, setJobDescription] = useState("");

  return (
    <div className="app-container">
      <header>
        <h1>ðŸ“„ Resume Tailor</h1>
        <p>
          Customize your LaTeX resume, generate cover letters and cold emails
          with ease.
        </p>
        <div className="tabs">
          <button
            className={activeTab === "resume" ? "active" : ""}
            onClick={() => setActiveTab("resume")}
          >
            Resume
          </button>
          <button
            className={activeTab === "cover" ? "active" : ""}
            onClick={() => setActiveTab("cover")}
            disabled={!jobDescription.trim()}
          >
            Cover Letter
          </button>
          <button
            className={activeTab === "email" ? "active" : ""}
            onClick={() => setActiveTab("email")}
            disabled={!jobDescription.trim()}
          >
            Cold Email
          </button>
        </div>
      </header>

      <main>
        {activeTab === "resume" && <JDInput onJDUpdate={setJobDescription} />}
        {activeTab === "cover" && (
          <CoverLetter jobDescription={jobDescription} />
        )}
        {activeTab === "email" && <ColdEmail jobDescription={jobDescription} />}
      </main>
    </div>
  );
}

export default App;
