// ✅ promptBuilder.js
export function buildResumePrompt({ jobDescription, resumeTemplate }) {
  return `
You are an expert resume customization assistant. Given the LaTeX resume below and the job description, update only the placeholders marked with “% JD_...” (e.g., % JD_METLIFE_BULLET_1) to match the job description. Do not change any other parts.

--- Job Description ---
${jobDescription}

--- Full Resume Template (LaTeX) ---
${resumeTemplate.full}

Return only the full updated LaTeX content. Ensure the resume stays under one page and remains LaTeX formatted properly.
`;
}

export function buildCoverLetterPrompt({ jobDescription, resumeSummary }) {
  return `
You are an AI assistant. Based on the job description and the resume summary, write a concise, ATS-friendly cover letter.

--- Job Description ---
${jobDescription}

--- Resume Summary ---
${resumeSummary}

Only return the formatted cover letter content in plain text.
`;
}

export function buildColdEmailPrompt({ jobDescription, resumeSummary }) {
  return `
You're an expert in writing cold outreach emails. Based on the resume and job description, write a professional and personalized email for reaching out to an HR or recruiter.

--- Job Description ---
${jobDescription}

--- Resume Summary ---
${resumeSummary}

Only return the email body. Do not include subject line or resume.
`;
}
