import React, { useState } from "react";
import { buildATSAnalysisPrompt } from "../utils/promptBuilder";
import { callGeminiATSAPI } from "../utils/apiHandler";
import { useNavigate } from "react-router-dom";

import "../CSS/ATSAnalysis.css";

function ATSAnalysis({ jobDesc }) {
  const [resumeText, setResumeText] = useState("");
  const [atsScore, setAtsScore] = useState(null);
  const [gaps, setGaps] = useState([]);
  const [improvements, setImprovements] = useState([]);
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleAnalyze = async () => {
    if (!resumeText.trim() || !jobDesc.trim()) return;

    setLoading(true);
    const prompt = buildATSAnalysisPrompt({
      resumeLatex: resumeText,
      jobDescription: jobDesc,
    });

    const result = await callGeminiATSAPI(prompt);

    setAtsScore(result.atsScore);
    setGaps(result.gaps || []);
    setImprovements(result.improvements || []);
    setSummary(result.summary || "");
    setLoading(false);
  };

  return (
    <div className="ats-container">
      <h2 className="ats-header">üìä Resume vs JD - ATS Analysis</h2>

      <textarea className="ats-textarea" />

      <button>Run ATS Analysis</button>
      <button className="ats-button" onClick={handleAnalyze} disabled={loading}>
        {loading ? "Analyzing..." : "Run ATS Analysis"}
      </button>

      <div className="ats-result-box">
        <h3 className="ats-section-title">üí° Suggestions & ATS Insights</h3>
        <p>
          <strong>‚úÖ ATS Score:</strong> {atsScore || "N/A"}
        </p>

        {gaps.length > 0 && (
          <>
            <h4 className="ats-section-title">üö´ Gaps or Mismatches:</h4>
            <ul className="ats-list">
              {gaps.map((gap, idx) => (
                <li key={idx}>{gap}</li>
              ))}
            </ul>
          </>
        )}

        {improvements.length > 0 && (
          <>
            <h4 className="ats-section-title">üõ† Suggested Improvements:</h4>
            <ul className="ats-list">
              {improvements.map((tip, idx) => (
                <li key={idx}>{tip}</li>
              ))}
            </ul>
          </>
        )}

        {summary && (
          <>
            <h4 className="ats-section-title">üìù Summary:</h4>
            <div className="ats-summary">{summary}</div>
          </>
        )}
      </div>
    </div>
  );
}

export default ATSAnalysis;
