import React, { useState, useEffect, useRef } from "react";
import "../CSS/CoverLetter.css";
import html2pdf from "html2pdf.js";

const CoverLetterEditorModal = ({ isOpen, onClose, content, onChange }) => {
  const [editorContent, setEditorContent] = useState(content);

  const pdfRef = useRef();
  // üÜï New: Manage the content internally to prevent cursor jumps
  useEffect(() => {
    setEditorContent(content);
  }, [content]);
  const handleInput = (e) => {
    setEditorContent(e.currentTarget.innerHTML);
    // You can optionally call the parent's onChange if you need to sync it.
    // For now, let's keep it simple to fix the cursor issue.
  };

  const exportPDF = () => {
    const element = pdfRef.current;
    if (!element.textContent.trim()) {
      alert("No content to export!");
      return;
    }
    const opt = {
      margin: 0,
      filename: "CoverLetter.pdf",
      image: { type: "jpeg", quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: "in", format: "a4", orientation: "portrait" },
    };
    html2pdf().set(opt).from(element).save();
  };

  const handleCommand = (command, value = null) => {
    if (pdfRef.current) {
      document.execCommand(command, false, value);
      setEditorContent(pdfRef.current.innerHTML);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="editor-overlay">
      <div className="editor-modal-container">
        <div className="editor-header">
          <h2>Cover Letter Editor</h2>
          <div className="action-buttons">
            <button onClick={exportPDF}>üì§ Export PDF</button>
            <button onClick={onClose}>‚ùå Close</button>
          </div>
        </div>
        <div className="editor-toolbar">
          <button>B</button>
          <button>I</button>
        </div>
        <div className="editor-scroll-container">
          <div
            className="editor-page-view"
            ref={pdfRef}
            contentEditable
            suppressContentEditableWarning={true}
            // 4. Use the internal state and the new handler.
            // dangerouslySetInnerHTML={{ __html: editorContent }}
            onInput={handleInput}
          />
        </div>
      </div>
    </div>
  );
};

export default CoverLetterEditorModal;
