import React, { useState } from "react";
import { buildATSAnalysisPrompt } from "../utils/promptBuilder";
import { callGeminiATSAPI } from "../utils/apiHandler";

function ATSAnalysis({ jobDesc }) {
  const [resumeText, setResumeText] = useState("");
  const [atsScore, setAtsScore] = useState(null);
  const [gaps, setGaps] = useState([]);
  const [improvements, setImprovements] = useState([]);
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  const handleAnalyze = async () => {
    if (!resumeText.trim() || !jobDesc.trim()) return;

    setLoading(true);
    const prompt = buildATSAnalysisPrompt({
      resumeLatex: resumeText,
      jobDescription: jobDesc,
    });

    const result = await callGeminiATSAPI(prompt);

    setAtsScore(result.atsScore);
    setGaps(result.gaps || []);
    setImprovements(result.improvements || []);
    setSummary(result.summary || "");
    setLoading(false);
  };

  return (
    <div style={{ padding: "1.5rem" }}>
         <button
            style={{
              marginTop: "1rem",
              backgroundColor: "#004aad",
              color: "white",
              padding: "0.5rem 1rem",
              border: "none",
              borderRadius: "4px",
            }}
            onClick={() => navigate("/ats-analysis")}
          ></button>
      <h2>üìä Resume vs JD - ATS Analysis</h2>
      <textarea
        rows={12}
        placeholder="Paste your LaTeX resume code here..."
        value={resumeText}
        onChange={(e) => setResumeText(e.target.value)}
        style={{ width: "100%", background: "#f9f9f9", marginBottom: "1rem" }}
      />
      <button onClick={handleAnalyze} disabled={loading}>
        {loading ? "Analyzing..." : "Run ATS Analysis"}
      </button>

      {(atsScore || gaps.length || improvements.length || summary) && (
        <div style={{ marginTop: "1.5rem" }}>
          <h3>üí° Suggestions & ATS Insights</h3>
          <div
            style={{
              background: "#e0ffe0",
              padding: "1rem",
              borderRadius: "8px",
            }}
          >
            <p>
              <strong>‚úÖ ATS Score:</strong> {atsScore || "N/A"}
            </p>

            {gaps.length > 0 && (
              <>
                <h4>üö´ Gaps or Mismatches:</h4>
                <ul>
                  {gaps.map((gap, idx) => (
                    <li key={idx}>{gap}</li>
                  ))}
                </ul>
              </>
            )}

            {improvements.length > 0 && (
              <>
                <h4>üõ† Suggested Improvements:</h4>
                <ul>
                  {improvements.map((tip, idx) => (
                    <li key={idx}>{tip}</li>
                  ))}
                </ul>
              </>
            )}

            {summary && (
              <>
                <h4>üìù Summary:</h4>
                <p>{summary}</p>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default ATSAnalysis;
