import axios from "axios";

const GEMINI_API_URL =
  "https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent";

export async function callGeminiAPI(prompt) {
  const apiKey = "AIzaSyB1mo0mYLvRkle-6OCLUFpS0rbaSdIHiHI"; // Replace if needed

  console.log("üì° Calling Gemini API...");

  try {
    const response = await axios.post(
      `${GEMINI_API_URL}?key=${apiKey}`,
      {
        contents: [
          {
            role: "user",
            parts: [{ text: prompt }],
          },
        ],
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    const text = response.data.candidates?.[0]?.content?.parts?.[0]?.text || "";

    // Split LaTeX and changes summary using the delimiter
    const [latex, summaryRaw = ""] = text.split("===CHANGES===");

    const updatedLatex = latex.trim();
    const changesSummary = summaryRaw
      .trim()
      .split("\n")
      .map((line) => line.trim())
      .filter(Boolean);

    console.log("‚úÖ Gemini LaTeX Output:", updatedLatex.slice(0, 300) + "...");
    console.log("üìù Gemini Summary:", changesSummary);

    return {
      updatedLatex,
      changesSummary,
    };
  } catch (error) {
    console.error(
      "‚ùå Gemini API Error:",
      error.response?.data || error.message
    );
    alert(
      "Gemini API Error: " +
        JSON.stringify(error.response?.data || error.message)
    );
    return {
      updatedLatex: "Error generating LaTeX.",
      changesSummary: [],
    };
  }
}
