// src/components/ResumeGeneration.js
import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../CSS/ResumeGeneration.css";

function ResumeGeneration() {
  const location = useLocation();
  const navigate = useNavigate();

  // Get the data passed from JDInput
  const { resumeData, jobDescription, companyName } = location.state || {};

  // State for editable fields
  const [editableData, setEditableData] = useState({
    PROFESSIONAL_SUMMARY: "",
    TECHNICAL_SKILLS: "",
    METLIFE_BULLET_POINTS: "",
    ADONS_BULLET_POINTS: "",
  });

  // State for PDF generation
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [pdfUrl, setPdfUrl] = useState(null);
  const [isEditing, setIsEditing] = useState({
    PROFESSIONAL_SUMMARY: false,
    TECHNICAL_SKILLS: false,
    METLIFE_BULLET_POINTS: false,
    ADONS_BULLET_POINTS: false,
  });

  // Initialize editable data when component mounts
  useEffect(() => {
    if (resumeData) {
      setEditableData({
        PROFESSIONAL_SUMMARY: resumeData.PROFESSIONAL_SUMMARY || "",
        TECHNICAL_SKILLS: resumeData.TECHNICAL_SKILLS || "",
        METLIFE_BULLET_POINTS: resumeData.METLIFE_BULLET_POINTS || "",
        ADONS_BULLET_POINTS: resumeData.ADONS_BULLET_POINTS || "",
      });
      setMessage(
        "‚úÖ Resume data loaded! You can edit the content before generating PDF."
      );
    } else {
      setMessage("‚ùå No resume data found. Please generate content first.");
    }
  }, [resumeData]);

  // Handle field editing
  const handleEdit = (field) => {
    setIsEditing((prev) => ({ ...prev, [field]: true }));
  };

  const handleSave = (field) => {
    setIsEditing((prev) => ({ ...prev, [field]: false }));
    setMessage(
      "üíæ Changes saved locally. Click 'Generate PDF' to create your resume."
    );
  };

  const handleFieldChange = (field, value) => {
    setEditableData((prev) => ({ ...prev, [field]: value }));
  };

  // Generate PDF using our Spring Boot backend
  const handleGeneratePDF = async () => {
    if (!editableData.PROFESSIONAL_SUMMARY) {
      setMessage("‚ùå Please add content before generating PDF");
      return;
    }

    setIsLoading(true);
    setMessage("‚è≥ Generating your PDF...");

    try {
      const response = await axios.post(
        "http://localhost:8080/api/resume/generate",
        editableData,
        {
          responseType: "blob",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      // Create blob and URL for viewing
      const blob = new Blob([response.data], { type: "application/pdf" });
      const url = window.URL.createObjectURL(blob);

      setPdfUrl(url);
      setMessage("‚úÖ Resume PDF generated successfully!");
    } catch (error) {
      console.error("‚ùå Error generating PDF:", error);
      setMessage(
        "‚ùå Error generating PDF. Make sure the backend is running on port 8080."
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Download PDF
  const handleDownloadPDF = () => {
    if (pdfUrl) {
      const link = document.createElement("a");
      link.href = pdfUrl;
      link.download = `${companyName || "resume"}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      setMessage("üìÑ PDF downloaded successfully!");
    }
  };

  // Print PDF
  const handlePrintPDF = () => {
    if (pdfUrl) {
      const printWindow = window.open(pdfUrl, "_blank");
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  // Go back to JDInput
  const handleGoBack = () => {
    navigate("/jdinput");
  };

  // Copy to clipboard
  const handleCopy = (field) => {
    navigator.clipboard.writeText(editableData[field]);
    setMessage(`üìã ${field.replace(/_/g, " ")} copied to clipboard!`);
    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div className="resume-generation-container">
      {/* Header */}
      <header className="resume-header">
        <button onClick={handleGoBack} className="back-btn">
          ‚Üê Back to Job Analysis
        </button>
        <div className="header-content">
          <h1>üìÑ Resume Generation</h1>
          <p>Edit your content and generate professional PDF resume</p>
        </div>
        <div className="header-actions">
          {companyName && (
            <span className="company-badge">üè¢ {companyName}</span>
          )}
        </div>
      </header>

      {/* Main Grid Layout - Two Columns */}
      <div className="main-grid">
        {/* Left Panel - Editable Content */}
        <div className="left-panel">
          <div className="panel-header">
            <h2>‚úèÔ∏è Edit Your Resume Content</h2>
            <p className="helper-text">
              Click on any section to edit before generating PDF
            </p>
          </div>

          {/* Professional Summary Section */}
          <div className="editable-section">
            <div className="section-header">
              <h3>üìù Professional Summary</h3>
              <div className="section-actions">
                {!isEditing.PROFESSIONAL_SUMMARY ? (
                  <>
                    <button
                      onClick={() => handleEdit("PROFESSIONAL_SUMMARY")}
                      className="edit-btn"
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      onClick={() => handleCopy("PROFESSIONAL_SUMMARY")}
                      className="copy-btn"
                    >
                      üìã Copy
                    </button>
                  </>
                ) : (
                  <button
                    onClick={() => handleSave("PROFESSIONAL_SUMMARY")}
                    className="save-btn"
                  >
                    üíæ Save
                  </button>
                )}
              </div>
            </div>
            {isEditing.PROFESSIONAL_SUMMARY ? (
              <textarea
                className="edit-textarea"
                value={editableData.PROFESSIONAL_SUMMARY}
                onChange={(e) =>
                  handleFieldChange("PROFESSIONAL_SUMMARY", e.target.value)
                }
                rows="6"
                placeholder="Enter your professional summary LaTeX content..."
              />
            ) : (
              <div className="content-preview">
                <pre>
                  {editableData.PROFESSIONAL_SUMMARY || "No content yet"}
                </pre>
              </div>
            )}
          </div>

          {/* Technical Skills Section */}
          <div className="editable-section">
            <div className="section-header">
              <h3>üõ†Ô∏è Technical Skills</h3>
              <div className="section-actions">
                {!isEditing.TECHNICAL_SKILLS ? (
                  <>
                    <button
                      onClick={() => handleEdit("TECHNICAL_SKILLS")}
                      className="edit-btn"
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      onClick={() => handleCopy("TECHNICAL_SKILLS")}
                      className="copy-btn"
                    >
                      üìã Copy
                    </button>
                  </>
                ) : (
                  <button
                    onClick={() => handleSave("TECHNICAL_SKILLS")}
                    className="save-btn"
                  >
                    üíæ Save
                  </button>
                )}
              </div>
            </div>
            {isEditing.TECHNICAL_SKILLS ? (
              <textarea
                className="edit-textarea"
                value={editableData.TECHNICAL_SKILLS}
                onChange={(e) =>
                  handleFieldChange("TECHNICAL_SKILLS", e.target.value)
                }
                rows="6"
                placeholder="Enter your technical skills LaTeX table content..."
              />
            ) : (
              <div className="content-preview">
                <pre>{editableData.TECHNICAL_SKILLS || "No content yet"}</pre>
              </div>
            )}
          </div>

          {/* MetLife Experience Section */}
          <div className="editable-section">
            <div className="section-header">
              <h3>üè¢ MetLife Experience</h3>
              <div className="section-actions">
                {!isEditing.METLIFE_BULLET_POINTS ? (
                  <>
                    <button
                      onClick={() => handleEdit("METLIFE_BULLET_POINTS")}
                      className="edit-btn"
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      onClick={() => handleCopy("METLIFE_BULLET_POINTS")}
                      className="copy-btn"
                    >
                      üìã Copy
                    </button>
                  </>
                ) : (
                  <button
                    onClick={() => handleSave("METLIFE_BULLET_POINTS")}
                    className="save-btn"
                  >
                    üíæ Save
                  </button>
                )}
              </div>
            </div>
            {isEditing.METLIFE_BULLET_POINTS ? (
              <textarea
                className="edit-textarea"
                value={editableData.METLIFE_BULLET_POINTS}
                onChange={(e) =>
                  handleFieldChange("METLIFE_BULLET_POINTS", e.target.value)
                }
                rows="8"
                placeholder="Enter MetLife experience bullet points..."
              />
            ) : (
              <div className="content-preview">
                <pre>
                  {editableData.METLIFE_BULLET_POINTS || "No content yet"}
                </pre>
              </div>
            )}
          </div>

          {/* Adons Experience Section */}
          <div className="editable-section">
            <div className="section-header">
              <h3>üè≠ Adons Soft Tech Experience</h3>
              <div className="section-actions">
                {!isEditing.ADONS_BULLET_POINTS ? (
                  <>
                    <button
                      onClick={() => handleEdit("ADONS_BULLET_POINTS")}
                      className="edit-btn"
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      onClick={() => handleCopy("ADONS_BULLET_POINTS")}
                      className="copy-btn"
                    >
                      üìã Copy
                    </button>
                  </>
                ) : (
                  <button
                    onClick={() => handleSave("ADONS_BULLET_POINTS")}
                    className="save-btn"
                  >
                    üíæ Save
                  </button>
                )}
              </div>
            </div>
            {isEditing.ADONS_BULLET_POINTS ? (
              <textarea
                className="edit-textarea"
                value={editableData.ADONS_BULLET_POINTS}
                onChange={(e) =>
                  handleFieldChange("ADONS_BULLET_POINTS", e.target.value)
                }
                rows="8"
                placeholder="Enter Adons experience bullet points..."
              />
            ) : (
              <div className="content-preview">
                <pre>
                  {editableData.ADONS_BULLET_POINTS || "No content yet"}
                </pre>
              </div>
            )}
          </div>

          {/* Generate Button */}
          <div className="generate-section">
            <button
              onClick={handleGeneratePDF}
              disabled={!editableData.PROFESSIONAL_SUMMARY || isLoading}
              className="generate-pdf-btn"
            >
              {isLoading ? (
                <>
                  <span className="spinner-small"></span>
                  Generating PDF...
                </>
              ) : (
                <>üéØ Generate PDF Resume</>
              )}
            </button>
          </div>
        </div>

        {/* Right Panel - PDF Preview */}
        <div className="right-panel">
          <div className="panel-header">
            <h2>üìÑ PDF Preview</h2>
            {pdfUrl && (
              <div className="pdf-actions">
                <button
                  onClick={handleDownloadPDF}
                  className="action-btn download"
                >
                  üíæ Download
                </button>
                <button onClick={handlePrintPDF} className="action-btn print">
                  üñ®Ô∏è Print
                </button>
              </div>
            )}
          </div>

          <div className="pdf-preview-container">
            {pdfUrl ? (
              <iframe
                src={pdfUrl}
                className="pdf-iframe"
                title="Resume Preview"
              />
            ) : (
              <div className="no-preview">
                <div className="no-preview-content">
                  <span className="preview-icon">üìÑ</span>
                  <h3>No Preview Available</h3>
                  <p>
                    Edit your content and click "Generate PDF Resume" to see the
                    preview
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Status Message */}
      {message && (
        <div
          className={`status-message ${
            message.includes("‚úÖ")
              ? "success"
              : message.includes("‚ùå")
              ? "error"
              : message.includes("‚è≥")
              ? "loading"
              : "info"
          }`}
        >
          {message}
        </div>
      )}
    </div>
  );
}

export default ResumeGeneration;
