// âœ… Updated ResumeGeneration.js
import React, { useState } from "react";
import "../CSS/ResumeGeneration.css";

const ResumeGeneration = ({
  summaryLatex,
  skillsLatex,
  metlifeLatex,
  adonsLatex,
  onGeneratePDF,
}) => {
  const [summary, setSummary] = useState(summaryLatex);
  const [skills, setSkills] = useState(skillsLatex);
  const [metlife, setMetlife] = useState(metlifeLatex);
  const [adons, setAdons] = useState(adonsLatex);

  const handleGenerate = () => {
    onGeneratePDF({ summary, skills, metlife, adons });
  };

  return (
    <div className="resume-gen-container">
      <div className="resume-gen-left">
        <h2>Your Personal Info</h2>
        <div className="form-group">
          <label>Summary (LaTeX)</label>
          <textarea
            value={summary}
            onChange={(e) => setSummary(e.target.value)}
            rows={5}
          />
        </div>
        <div className="form-group">
          <label>Skills (LaTeX)</label>
          <textarea
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
            rows={5}
          />
        </div>
        <div className="form-group">
          <label>MetLife Experience (LaTeX)</label>
          <textarea
            value={metlife}
            onChange={(e) => setMetlife(e.target.value)}
            rows={5}
          />
        </div>
        <div className="form-group">
          <label>Adons Experience (LaTeX)</label>
          <textarea
            value={adons}
            onChange={(e) => setAdons(e.target.value)}
            rows={5}
          />
        </div>
        <button className="generate-btn" onClick={handleGenerate}>
          ðŸ“„ Generate Resume
        </button>
      </div>

      <div className="resume-gen-right">
        <h2>Preview</h2>
        <iframe
          title="PDF Preview"
          className="pdf-preview"
          src="http://localhost:8080/generated-resume.pdf"
        ></iframe>
      </div>
    </div>
  );
};

export default ResumeGeneration;
