import React, { useState } from "react";
import { sendCustomEmails } from "../services/coldEmailService"; // Updated import

const ColdEmailModal = ({ isOpen, onClose, coldEmail, companyName }) => {
  const [contacts, setContacts] = useState([]);
  const [newContact, setNewContact] = useState({
    name: "",
    email: "",
    title: "",
  });
  const [candidateInfo, setCandidateInfo] = useState({
    candidateName: "Mahaboob Pasha Mohammad",
    candidateEmail: "mahaboobpashamohammad@gmail.com",
    candidatePhone: "3143056056",
  });
  const [sending, setSending] = useState(false);

  // Extract subject and body from coldEmail
  const emailSubject =
    coldEmail.match(/Subject:\s*(.*)/i)?.[1]?.trim() || "Job Application";
  const emailBody = coldEmail.replace(/Subject:.*\n?/i, "").trim();

  const addContact = () => {
    if (newContact.name && newContact.email) {
      const contact = {
        name: newContact.title
          ? `${newContact.name} (${newContact.title})`
          : newContact.name,
        email: newContact.email,
        company: companyName,
      };

      setContacts([...contacts, contact]);
      setNewContact({ name: "", email: "", title: "" });
    }
  };

  const removeContact = (index) => {
    setContacts(contacts.filter((_, i) => i !== index));
  };

  // Updated handleSendEmails function
  const handleSendEmails = async () => {
    if (contacts.length === 0) {
      alert("Please add at least one HR contact.");
      return;
    }

    setSending(true);
    try {
      console.log("Sending custom emails with data:", {
        candidateName: candidateInfo.candidateName,
        candidateEmail: candidateInfo.candidateEmail,
        candidatePhone: candidateInfo.candidatePhone,
        companyName: companyName,
        customSubject: emailSubject,
        customBody: emailBody,
        selectedContacts: contacts,
      });

      const response = await sendCustomEmails({
        candidateName: candidateInfo.candidateName,
        candidateEmail: candidateInfo.candidateEmail,
        candidatePhone: candidateInfo.candidatePhone,
        companyName: companyName,
        customSubject: emailSubject,
        customBody: emailBody,
        selectedContacts: contacts,
      });

      console.log("Backend response:", response.data);

      if (response.data.status === "success") {
        alert(
          `‚úÖ Success! ${response.data.successCount} emails sent successfully out of ${response.data.totalEmails} total emails!`
        );
        onClose();

        // Clear contacts for next use
        setContacts([]);
      } else {
        alert(
          `‚ùå ${
            response.data.message ||
            "Some emails failed to send. Check console for details."
          }`
        );
      }
    } catch (error) {
      console.error("Send error:", error);

      // More detailed error handling
      if (error.response) {
        alert(
          `‚ùå Backend error: ${error.response.data.message || "Unknown error"}`
        );
      } else if (error.request) {
        alert(
          "‚ùå Cannot connect to backend. Make sure Spring Boot is running on port 8080."
        );
      } else {
        alert(`‚ùå Error: ${error.message}`);
      }
    } finally {
      setSending(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
      }}
    >
      <div
        style={{
          backgroundColor: "white",
          width: "90%",
          maxWidth: "600px",
          maxHeight: "80vh",
          borderRadius: "10px",
          padding: "20px",
          overflow: "auto",
        }}
      >
        {/* Header */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "20px",
            borderBottom: "1px solid #eee",
            paddingBottom: "10px",
          }}
        >
          <h2 style={{ margin: 0 }}>üöÄ Send Cold Email - {companyName}</h2>
          <button
            onClick={onClose}
            style={{
              background: "none",
              border: "none",
              fontSize: "24px",
              cursor: "pointer",
            }}
          >
            √ó
          </button>
        </div>

        {/* Candidate Info */}
        <div style={{ marginBottom: "20px" }}>
          <h3>Your Information</h3>
          <div style={{ display: "grid", gap: "10px" }}>
            <input
              type="text"
              placeholder="Your Name"
              value={candidateInfo.candidateName}
              onChange={(e) =>
                setCandidateInfo({
                  ...candidateInfo,
                  candidateName: e.target.value,
                })
              }
              style={{
                padding: "8px",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <input
              type="email"
              placeholder="Your Email"
              value={candidateInfo.candidateEmail}
              onChange={(e) =>
                setCandidateInfo({
                  ...candidateInfo,
                  candidateEmail: e.target.value,
                })
              }
              style={{
                padding: "8px",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <input
              type="tel"
              placeholder="Your Phone"
              value={candidateInfo.candidatePhone}
              onChange={(e) =>
                setCandidateInfo({
                  ...candidateInfo,
                  candidatePhone: e.target.value,
                })
              }
              style={{
                padding: "8px",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
          </div>
        </div>

        {/* Add HR Contacts */}
        <div style={{ marginBottom: "20px" }}>
          <h3>Add HR Contacts</h3>
          <div style={{ display: "grid", gap: "10px", marginBottom: "10px" }}>
            <input
              type="text"
              placeholder="HR Name"
              value={newContact.name}
              onChange={(e) =>
                setNewContact({ ...newContact, name: e.target.value })
              }
              style={{
                padding: "8px",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <input
              type="email"
              placeholder="HR Email"
              value={newContact.email}
              onChange={(e) =>
                setNewContact({ ...newContact, email: e.target.value })
              }
              style={{
                padding: "8px",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <input
              type="text"
              placeholder="Job Title (optional)"
              value={newContact.title}
              onChange={(e) =>
                setNewContact({ ...newContact, title: e.target.value })
              }
              style={{
                padding: "8px",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
          </div>
          <button
            onClick={addContact}
            disabled={!newContact.name || !newContact.email}
            style={{
              padding: "8px 16px",
              backgroundColor: "#10b981",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Add Contact
          </button>
        </div>

        {/* Contact List */}
        {contacts.length > 0 && (
          <div style={{ marginBottom: "20px" }}>
            <h4>HR Contacts ({contacts.length})</h4>
            {contacts.map((contact, index) => (
              <div
                key={index}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  padding: "8px",
                  backgroundColor: "#f9fafb",
                  marginBottom: "5px",
                  borderRadius: "4px",
                }}
              >
                <div>
                  <div style={{ fontWeight: "500" }}>{contact.name}</div>
                  <div style={{ fontSize: "12px", color: "#666" }}>
                    {contact.email}
                  </div>
                </div>
                <button
                  onClick={() => removeContact(index)}
                  style={{
                    background: "none",
                    border: "none",
                    color: "#ef4444",
                    cursor: "pointer",
                  }}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Email Preview */}
        <div style={{ marginBottom: "20px" }}>
          <h3>Email Preview</h3>
          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "4px",
              padding: "10px",
              backgroundColor: "#f9fafb",
            }}
          >
            <div style={{ marginBottom: "8px" }}>
              <strong>Subject:</strong> {emailSubject}
            </div>
            <div
              style={{
                whiteSpace: "pre-line",
                fontSize: "14px",
                maxHeight: "150px",
                overflow: "auto",
              }}
            >
              {emailBody}
            </div>
          </div>
        </div>

        {/* Send Button */}
        <div
          style={{
            borderTop: "1px solid #eee",
            paddingTop: "15px",
            display: "flex",
            justifyContent: "flex-end",
            gap: "10px",
          }}
        >
          <button
            onClick={onClose}
            style={{
              padding: "10px 20px",
              backgroundColor: "#6b7280",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Cancel
          </button>
          <button
            onClick={handleSendEmails}
            disabled={sending || contacts.length === 0}
            style={{
              padding: "10px 20px",
              backgroundColor: contacts.length > 0 ? "#3b82f6" : "#9ca3af",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: contacts.length > 0 ? "pointer" : "not-allowed",
            }}
          >
            {sending ? "Sending..." : `Send to ${contacts.length} Contact(s)`}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ColdEmailModal;
