export function sanitizeLatexContent(content) {
  if (!content) return "";

  // Escape unescaped % manually (no lookbehind)
  content = content.replace(/(^|[^\\])%/g, (match, p1) => `${p1}\\%`);

  // Escape unescaped &
  content = content.replace(/(^|[^\\])&/g, (match, p1) => `${p1}\\&`);

  // Replace double asterisks around percentages with LaTeX bold
  content = content.replace(/\*\*(\d+(\.\d+)?)%?\*\*/g, (_, num) => `\\textbf{${num}\\%}`);

  // Convert 45% to \textbf{45\%} if not already bolded
  content = content.replace(/(\d+(\.\d+)?)%/g, (_, num) => `\\textbf{${num}\\%}`);

  // Remove any standalone \ at end of line (not valid in LaTeX)
  content = content.replace(/\\\s*$/gm, "");
  return content;
}

