import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useResume } from "../context/ResumeContext";
import "../CSS/WhyIFitGeneration.css"; // We'll create this CSS file

function WhyIFitGeneration() {
  const location = useLocation();
  const navigate = useNavigate();
  const { companyName, jobDesc } = useResume();

  // Get data passed from JDInput component
  const passedData = location.state || {};
  const initialWhyIFitContent = passedData.whyIFitData || "";
  const initialCompanyName = passedData.companyName || companyName || "";
  const initialJobDescription = passedData.jobDescription || jobDesc || "";

  // State for form fields
  const [formData, setFormData] = useState({
    date: new Date().toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    }),
    targetCompany: initialCompanyName,
    candidateName: "Mahaboob Pasha Mohammad",
    candidateEmail: "mahaboobpashamohammad8@gmail.com",
    portfolioUrl: "https://miabu-pashh.github.io/Portfolio-maibu/",
    linkedinUrl: "https://www.linkedin.com/in/mohammad-mahaboob-pasha/",
    githubUrl: "https://github.com/miabu-pashh",
    calendlyUrl: "https://calendly.com/mahaboobpashamohammad8/30min",
  });

  const [whyIFitContent, setWhyIFitContent] = useState(initialWhyIFitContent);
  const [generatedLatex, setGeneratedLatex] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);
  const [previewContent, setPreviewContent] = useState("");

  // Status message
  const [statusMessage, setStatusMessage] = useState("");

  useEffect(() => {
    if (initialWhyIFitContent) {
      setStatusMessage(
        '✅ Why I Fit content loaded! Fill in details and click "MAKE" to generate PDF.'
      );
      generatePreview();
    } else {
      setStatusMessage(
        "No Why I Fit content found. Please go back and generate content first."
      );
    }
  }, [initialWhyIFitContent]);

  const handleInputChange = (field, value) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const generatePreview = () => {
    // Create preview content combining the AI-generated content with form data
    const preview = `Why I Fit For This Role - ${formData.targetCompany}
Date: ${formData.date}
Candidate: ${formData.candidateName}

${whyIFitContent}

Contact Information:
📧 ${formData.candidateEmail}
🌐 Portfolio: ${formData.portfolioUrl}
💼 LinkedIn: ${formData.linkedinUrl}
🔗 GitHub: ${formData.githubUrl}
📅 Schedule Call: ${formData.calendlyUrl}`;

    setPreviewContent(preview);
  };

  useEffect(() => {
    generatePreview();
  }, [formData, whyIFitContent]);

  const handleGenerate = async () => {
    if (!whyIFitContent.trim()) {
      alert(
        "No Why I Fit content available. Please go back and generate content first."
      );
      return;
    }

    setIsGenerating(true);
    setStatusMessage("🔄 Generating Why I Fit LaTeX document...");

    try {
      // Call the backend API to generate LaTeX document
      const response = await fetch(
        "http://localhost:8080/api/why-i-fit/generate",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            jobDescription: initialJobDescription,
            companyName: formData.targetCompany,
            candidateName: formData.candidateName,
            candidateEmail: formData.candidateEmail,
            geminiOutput: whyIFitContent,
          }),
        }
      );

      const data = await response.json();

      if (data.status === "success") {
        setGeneratedLatex(data.data.finalLatexContent);
        setStatusMessage("✅ Why I Fit LaTeX document generated successfully!");

        // You can add PDF generation logic here similar to your cover letter
        // For now, we'll just show the LaTeX content
      } else {
        setStatusMessage("❌ Failed to generate document: " + data.message);
      }
    } catch (error) {
      console.error("Error generating Why I Fit document:", error);
      setStatusMessage("❌ Error generating document. Please try again.");
    } finally {
      setIsGenerating(false);
    }
  };

  const handleDownloadLatex = () => {
    if (!generatedLatex) {
      alert("Please generate the document first");
      return;
    }

    const blob = new Blob([generatedLatex], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `Why_I_Fit_${formData.targetCompany.replace(/\s+/g, "_")}.tex`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="why-i-fit-generation">
      <header className="header">
        <h1>ResuMe</h1>
        <p>
          <strong>Why I Fit For This Role - Document Generator</strong>
        </p>
        <button onClick={() => navigate("/jdinput")} className="logout-btn">
          🏠 Home
        </button>
      </header>

      <div className="generation-container">
        {/* Left Panel - Form Fields */}
        <div className="left-panel">
          <h2>Document Details</h2>

          <div className="form-section">
            <label>Date</label>
            <input
              type="text"
              value={formData.date}
              onChange={(e) => handleInputChange("date", e.target.value)}
              className="form-input"
            />
          </div>

          <div className="form-section">
            <label>Target Company</label>
            <input
              type="text"
              value={formData.targetCompany}
              onChange={(e) =>
                handleInputChange("targetCompany", e.target.value)
              }
              className="form-input"
              placeholder="Company Name"
            />
          </div>

          <div className="form-section">
            <label>Your Name</label>
            <input
              type="text"
              value={formData.candidateName}
              onChange={(e) =>
                handleInputChange("candidateName", e.target.value)
              }
              className="form-input"
            />
          </div>

          <div className="form-section">
            <label>Your Email</label>
            <input
              type="email"
              value={formData.candidateEmail}
              onChange={(e) =>
                handleInputChange("candidateEmail", e.target.value)
              }
              className="form-input"
            />
          </div>

          <div className="form-section">
            <label>Portfolio URL</label>
            <input
              type="url"
              value={formData.portfolioUrl}
              onChange={(e) =>
                handleInputChange("portfolioUrl", e.target.value)
              }
              className="form-input"
            />
          </div>

          <div className="form-section">
            <label>LinkedIn Profile</label>
            <input
              type="url"
              value={formData.linkedinUrl}
              onChange={(e) => handleInputChange("linkedinUrl", e.target.value)}
              className="form-input"
            />
          </div>

          <div className="status-section">
            <p className="status-message">{statusMessage}</p>
          </div>

          <div className="button-section">
            <button
              onClick={handleGenerate}
              disabled={isGenerating || !whyIFitContent.trim()}
              className="make-btn"
            >
              {isGenerating ? "GENERATING..." : "MAKE"}
            </button>

            <button onClick={() => navigate("/jdinput")} className="home-btn">
              HOME
            </button>
          </div>

          {generatedLatex && (
            <div className="download-section">
              <button onClick={handleDownloadLatex} className="download-btn">
                📄 Download LaTeX File
              </button>
            </div>
          )}
        </div>

        {/* Middle Panel - AI Content Editor */}
        <div className="middle-panel">
          <h2>Why I Fit Content</h2>
          <textarea
            value={whyIFitContent}
            onChange={(e) => setWhyIFitContent(e.target.value)}
            className="content-editor"
            rows="20"
            placeholder="AI-generated Why I Fit content will appear here..."
          />
        </div>

        {/* Right Panel - Preview */}
        <div className="right-panel">
          <h2>Preview</h2>
          <div className="preview-container">
            <pre className="preview-content">{previewContent}</pre>
          </div>

          {generatedLatex && (
            <div className="latex-output">
              <h3>Generated LaTeX Code</h3>
              <textarea
                value={generatedLatex}
                readOnly
                className="latex-content"
                rows="15"
              />
              <button
                onClick={() => navigator.clipboard.writeText(generatedLatex)}
                className="copy-btn"
              >
                📋 Copy LaTeX Code
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default WhyIFitGeneration;
