// App.js
import React, { useState } from "react";
import JDInput from "./components/JDInput";
import CoverLetter from "./components/CoverLetter";
import ColdEmail from "./components/ColdEmail";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState("resume");

  return (
    <div className="app-container">
      <header>
        <h1>ðŸ“„ Resume Tailor</h1>
        <p>
          Customize your LaTeX resume, generate cover letters and cold emails
          with ease.
        </p>
        <div className="tabs">
          <button
            className={activeTab === "resume" ? "active" : ""}
            onClick={() => setActiveTab("resume")}
          >
            Resume
          </button>
          <button
            className={activeTab === "cover" ? "active" : ""}
            onClick={() => setActiveTab("cover")}
          >
            Cover Letter
          </button>
          <button
            className={activeTab === "email" ? "active" : ""}
            onClick={() => setActiveTab("email")}
          >
            Cold Email
          </button>
        </div>
      </header>

      <main>
        {activeTab === "resume" && <JDInput />}
        {activeTab === "cover" && <CoverLetter />}
        {activeTab === "email" && <ColdEmail />}
      </main>
    </div>
  );
}

export default App;
