import React, { useState, useEffect } from "react";
import {
  Mail,
  Calendar,
  Clock,
  AlertCircle,
  CheckCircle,
  Search,
  Plus,
} from "lucide-react";
import "../CSS/Emailman.css";
// API service for backend communication
const emailService = {
  async getStats() {
    const response = await fetch("http://localhost:8080/api/emails/stats");
    return response.json();
  },

  async getEmails(page = 0, size = 20, category = null) {
    let url = `http://localhost:8080/api/emails?page=${page}&size=${size}`;
    if (category) url += `&category=${category}`;
    const response = await fetch(url);
    return response.json();
  },

  async getCategories() {
    const response = await fetch("http://localhost:8080/api/emails/categories");
    return response.json();
  },

  async getPendingReminders() {
    const response = await fetch("http://localhost:8080/api/reminders/pending");
    return response.json();
  },

  async getUpcomingReminders() {
    const response = await fetch(
      "http://localhost:8080/api/reminders/upcoming"
    );
    return response.json();
  },

  async fetchGmailEmails() {
    const response = await fetch(
      "http://localhost:8080/api/emails/fetch-gmail",
      {
        method: "POST",
      }
    );
    return response.json();
  },
};

const EmailManagerDashboard = () => {
  const [stats, setStats] = useState({});
  const [emails, setEmails] = useState([]);
  const [categories, setCategories] = useState([]);
  const [reminders, setReminders] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [fetchingGmail, setFetchingGmail] = useState(false);

  useEffect(() => {
    loadDashboardData();
  }, []);
  // Update the loadDashboardData function
  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [statsData, emailsData, categoriesData, remindersData] =
        await Promise.all([
          emailService.getStats(),
          emailService.getEmails(),
          emailService.getCategories(),
          emailService.getUpcomingReminders(),
        ]);

      setStats(statsData || {});
      // Sort emails by date
      const sortedEmails = sortEmailsByDate(emailsData.content || []);
      setEmails(sortedEmails);
      setCategories(categoriesData || []);
      setReminders(remindersData || []);
    } catch (error) {
      console.error("Error loading dashboard data:", error);
      setStats({});
      setEmails([]);
      setCategories([]);
      setReminders([]);
    } finally {
      setLoading(false);
    }
  };

  // Also update the handleCategoryFilter function
  const handleCategoryFilter = async (category) => {
    setSelectedCategory(category);
    try {
      const emailsData = await emailService.getEmails(0, 20, category || null);
      // Sort filtered emails too
      const sortedEmails = sortEmailsByDate(emailsData.content || []);
      setEmails(sortedEmails);
    } catch (error) {
      console.error("Error filtering emails:", error);
      setEmails([]);
    }
  };

  const fetchGmailEmails = async () => {
    try {
      setFetchingGmail(true);
      const result = await emailService.fetchGmailEmails();
      console.log("Gmail fetch result:", result);

      // Show success/error message
      if (result.success) {
        alert("Gmail emails fetched successfully!");
      } else {
        alert("Error fetching Gmail emails: " + result.message);
      }

      // Reload dashboard data
      loadDashboardData();
    } catch (error) {
      console.error("Gmail fetch error:", error);
      alert("Error connecting to Gmail: " + error.message);
    } finally {
      setFetchingGmail(false);
    }
  };

  const getCategoryColor = (category) => {
    const colors = {
      INTERVIEW: "bg-blue-100 text-blue-800",
      ASSESSMENT: "bg-yellow-100 text-yellow-800",
      JOB_APPLICATION: "bg-green-100 text-green-800",
      FOLLOW_UP: "bg-purple-100 text-purple-800",
      OFFER: "bg-emerald-100 text-emerald-800",
      REJECTION: "bg-red-100 text-red-800",
      GENERAL: "bg-gray-100 text-gray-800",
      SPAM: "bg-orange-100 text-orange-800",
    };
    return colors[category] || "bg-gray-100 text-gray-800";
  };

  const getPriorityColor = (priority) => {
    const colors = {
      HIGH: "text-red-600",
      MEDIUM: "text-yellow-600",
      LOW: "text-green-600",
    };
    return colors[priority] || "text-gray-600";
  };

  const formatDate = (dateString) => {
    if (!dateString) return "No date";
    try {
      return new Date(dateString).toLocaleDateString("en-US", {
        month: "short",
        day: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      });
    } catch (error) {
      return "Invalid date";
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  const totalEmails = Object.values(stats.emailsByCategory || {}).reduce(
    (a, b) => a + b,
    0
  );
  const totalReminders = Object.values(stats.pendingReminders || {}).reduce(
    (a, b) => a + b,
    0
  );
  // Add this function after the existing helper functions
  const sortEmailsByDate = (emailList) => {
    return [...emailList].sort((a, b) => {
      const dateA = new Date(a.receivedDate);
      const dateB = new Date(b.receivedDate);
      return dateB - dateA; // Newest first
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <Mail className="h-8 w-8 text-blue-600 mr-3" />
              <h1 className="text-2xl font-bold text-gray-900">
                Email Manager
              </h1>
            </div>
            <div className="flex items-center space-x-4">
              <button
                onClick={fetchGmailEmails}
                disabled={fetchingGmail}
                className={`${
                  fetchingGmail
                    ? "bg-gray-400 cursor-not-allowed"
                    : "bg-green-600 hover:bg-green-700"
                } text-white px-4 py-2 rounded-lg flex items-center`}
              >
                <Mail className="h-4 w-4 mr-2" />
                {fetchingGmail ? "Fetching..." : "Fetch Gmail"}
              </button>
              <button className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center hover:bg-blue-700">
                <Plus className="h-4 w-4 mr-2" />
                Add Email
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Cards */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <div className="flex items-center">
            <Calendar className="h-5 w-5 text-blue-600 mr-2" />
            <span className="text-sm text-blue-800">
              Showing emails from the last 7 days â€¢ Total: {emails.length}{" "}
              emails
            </span>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Mail className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">
                  Total Emails
                </p>
                <p className="text-2xl font-semibold text-gray-900">
                  {totalEmails}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Calendar className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Interviews</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {(stats.emailsByCategory &&
                    stats.emailsByCategory.INTERVIEW) ||
                    0}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Clock className="h-8 w-8 text-yellow-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">
                  Pending Reminders
                </p>
                <p className="text-2xl font-semibold text-gray-900">
                  {totalReminders}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <AlertCircle className="h-8 w-8 text-red-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Assessments</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {(stats.emailsByCategory &&
                    stats.emailsByCategory.ASSESSMENT) ||
                    0}
                </p>
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Email List */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow">
              <div className="px-6 py-4 border-b border-gray-200">
                <div className="flex items-center justify-between">
                  <h2 className="text-lg font-medium text-gray-900">
                    Recent Emails
                  </h2>
                  <div className="flex items-center space-x-4">
                    {/* Search */}
                    <div className="relative">
                      <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                      <input
                        type="text"
                        placeholder="Search emails..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                    </div>

                    {/* Category Filter */}
                    <select
                      value={selectedCategory}
                      onChange={(e) => handleCategoryFilter(e.target.value)}
                      className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="">All Categories</option>
                      {categories.map((category) => (
                        <option key={category} value={category}>
                          {category ? category.replace(/_/g, " ") : category}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              </div>

              <div className="divide-y divide-gray-200">
                {emails.length === 0 ? (
                  <div className="px-6 py-12 text-center">
                    <Mail className="mx-auto h-12 w-12 text-gray-400" />
                    <h3 className="mt-2 text-sm font-medium text-gray-900">
                      No emails
                    </h3>
                    <p className="mt-1 text-sm text-gray-500">
                      Get started by clicking "Fetch Gmail" to import your
                      emails.
                    </p>
                  </div>
                ) : (
                  emails.map((email) => (
                    <div key={email.id} className="px-6 py-4 hover:bg-gray-50">
                      <div className="flex items-center justify-between">
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center">
                            <span
                              className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getCategoryColor(
                                email.category
                              )}`}
                            >
                              {email.category
                                ? email.category.replace(/_/g, " ")
                                : "Unknown"}
                            </span>
                            <span
                              className={`ml-2 text-sm font-medium ${getPriorityColor(
                                email.priority
                              )}`}
                            >
                              {email.priority || "No Priority"}
                            </span>
                            {!email.read && (
                              <span className="ml-2 w-2 h-2 bg-blue-600 rounded-full"></span>
                            )}
                          </div>
                          <p className="mt-1 text-sm font-medium text-gray-900 truncate">
                            {email.subject || "No Subject"}
                          </p>
                          <p className="text-sm text-gray-600">
                            From: {email.sender || "Unknown Sender"}
                          </p>
                          <p className="text-xs text-gray-500 mt-1">
                            {email.content
                              ? email.content.substring(0, 100) + "..."
                              : "No content"}
                          </p>
                        </div>
                        <div className="ml-4 flex-shrink-0 text-sm text-gray-500">
                          {formatDate(email.receivedDate)}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          {/* Sidebar - Reminders and Categories */}
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow">
              <div className="px-6 py-4 border-b border-gray-200">
                <h2 className="text-lg font-medium text-gray-900">
                  Upcoming Reminders
                </h2>
              </div>
              <div className="p-6">
                {reminders.length === 0 ? (
                  <div className="text-center">
                    <CheckCircle className="mx-auto h-8 w-8 text-gray-400" />
                    <p className="mt-2 text-sm text-gray-500">
                      No upcoming reminders
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {reminders.slice(0, 5).map((reminder) => (
                      <div
                        key={reminder.id}
                        className="border-l-4 border-blue-400 pl-4"
                      >
                        <p className="text-sm font-medium text-gray-900">
                          {reminder.title || "No Title"}
                        </p>
                        <p className="text-xs text-gray-600">
                          {formatDate(reminder.eventDateTime)}
                        </p>
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-blue-100 text-blue-800 mt-1">
                          {reminder.type
                            ? reminder.type.replace(/_/g, " ")
                            : "Unknown"}
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Category Summary */}
            <div className="bg-white rounded-lg shadow">
              <div className="px-6 py-4 border-b border-gray-200">
                <h2 className="text-lg font-medium text-gray-900">
                  Categories
                </h2>
              </div>
              <div className="p-6">
                <div className="space-y-3">
                  {Object.entries(stats.emailsByCategory || {}).map(
                    ([category, count]) => (
                      <div
                        key={category}
                        className="flex items-center justify-between"
                      >
                        <span
                          className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getCategoryColor(
                            category
                          )}`}
                        >
                          {category ? category.replace(/_/g, " ") : "Unknown"}
                        </span>
                        <span className="text-sm font-medium text-gray-900">
                          {count}
                        </span>
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmailManagerDashboard;
