import axios from "axios";


export async function callGeminiAPI(prompt) {
  const apiKey = "AIzaSyB1mo0mYLvRkle-6OCLUFpS0rbaSdIHiHI";

  try {
    const response = await axios.post(
      `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${apiKey}`,
      {
        contents: [
          {
            role: "user",
            parts: [
              {
                text: "Write a polite thank-you email to a recruiter after a phone interview.",
              },
            ],
          },
        ],
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    console.log("Response from Gemini:", response.data);
    return response.data.candidates?.[0]?.content?.parts?.[0]?.text || "";
  } catch (error) {
    console.error("Gemini API Error:", error.response?.data || error.message);
    alert(
      "Gemini API Error: " +
        JSON.stringify(error.response?.data || error.message)
    );
    return "Error generating content. Check API key or prompt.";
  }
}

