import JDInput from "./components/JDInput";
import CoverLetterEditor from "./components/CoverLetterEditor";
import ColdEmailGenerator from "./components/ColdEmailGenerator";

function App() {
  return (
    <div className="App">
      <h1>Resume Tailor App</h1>
      <div style={{ padding: "1rem" }}>
        <h2>Paste Job Description</h2>
        <p>
          Paste the job description here to generate tailored resume sections,
          cover letters, and cold emails.
        </p>
        <JDInput />
        <hr />
        <CoverLetterEditor />
        <hr />
        <ColdEmailGenerator />
      </div>
    </div>
  );
}

export default App;
