// src/components/WhyIFitGeneration.js
import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../CSS/WhyIFitGeneration.css";

function WhyIFitGeneration() {
  const location = useLocation();
  const navigate = useNavigate();

  // Get the data passed from JDInput
  const { whyIFitData, companyName, jobDescription } = location.state || {};

  // State for selected field
  const [selectedField, setSelectedField] = useState("TARGET_COMPANY");

  // State for editable content
  const [editableData, setEditableData] = useState({
    TARGET_COMPANY: "",
    TODAY_DATE: "",
    CANDIDATE_NAME: "Mahaboob Pasha Mohammad",
    CANDIDATE_EMAIL: "mahaboobpashamohammad8@gmail.com",
    PORTFOLIO_URL: "https://miabu-pashh.github.io/Portfolio-maibu/",
    LINKEDIN_URL: "https://www.linkedin.com/in/mohammad-mahaboob-pasha/",
    GITHUB_URL: "https://github.com/miabu-pashh",
    CALENDLY_URL: "https://calendly.com/mahaboobpashamohammad8/30min",
    WHY_I_FIT_CONTENT: "",
  });

  // State for PDF generation
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [pdfUrl, setPdfUrl] = useState(null);

  const fieldLabels = {
    TARGET_COMPANY: "Company Name",
    TODAY_DATE: "Date",
    CANDIDATE_NAME: "Your Name",
    CANDIDATE_EMAIL: "Your Email",
    PORTFOLIO_URL: "Portfolio URL",
    LINKEDIN_URL: "LinkedIn Profile",
    GITHUB_URL: "GitHub Profile",
    CALENDLY_URL: "Calendly Link",
    WHY_I_FIT_CONTENT: "Why I Fit Content",
  };

  // Initialize data when component mounts
  useEffect(() => {
    if (whyIFitData) {
      setEditableData({
        TARGET_COMPANY: companyName || "",
        TODAY_DATE: new Date().toLocaleDateString("en-US", {
          year: "numeric",
          month: "long",
          day: "numeric",
        }),
        CANDIDATE_NAME: "Mahaboob Pasha Mohammad",
        CANDIDATE_EMAIL: "mahaboobpashamohammad8@gmail.com",
        PORTFOLIO_URL: "https://miabu-pashh.github.io/Portfolio-maibu/",
        LINKEDIN_URL: "https://www.linkedin.com/in/mohammad-mahaboob-pasha/",
        GITHUB_URL: "https://github.com/miabu-pashh",
        CALENDLY_URL: "https://calendly.com/mahaboobpashamohammad8/30min",
        WHY_I_FIT_CONTENT: whyIFitData,
      });

      setMessage("✅ Why I Fit content loaded! Edit and generate PDF.");
    } else {
      setMessage("❌ No Why I Fit data found. Please generate content first.");
    }
  }, [whyIFitData, companyName]);

  // Handle field change
  const handleFieldChange = (field, value) => {
    setEditableData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  // Generate PDF using Spring Boot backend
  const handleGeneratePDF = async () => {
    if (!editableData.WHY_I_FIT_CONTENT) {
      setMessage("❌ Please add Why I Fit content before generating PDF");
      return;
    }

    setIsLoading(true);
    setMessage("⏳ Generating your Why I Fit PDF...");

    try {
      // First, generate the LaTeX document from your backend
      const latexResponse = await axios.post(
        "http://localhost:8080/api/why-i-fit/generate",
        {
          jobDescription: jobDescription,
          companyName: editableData.TARGET_COMPANY,
          candidateName: editableData.CANDIDATE_NAME,
          candidateEmail: editableData.CANDIDATE_EMAIL,
          geminiOutput: editableData.WHY_I_FIT_CONTENT,
        }
      );

      if (latexResponse.data.status === "success") {
        // Now send the complete LaTeX to PDF generation endpoint
        const pdfResponse = await axios.post(
          "http://localhost:8080/api/resume/generate-why-i-fit-pdf", // You'll need to create this endpoint
          {
            TARGET_COMPANY: editableData.TARGET_COMPANY,
            TODAY_DATE: editableData.TODAY_DATE,
            CANDIDATE_NAME: editableData.CANDIDATE_NAME,
            CANDIDATE_EMAIL: editableData.CANDIDATE_EMAIL,
            PORTFOLIO_URL: editableData.PORTFOLIO_URL,
            LINKEDIN_URL: editableData.LINKEDIN_URL,
            GITHUB_URL: editableData.GITHUB_URL,
            CALENDLY_URL: editableData.CALENDLY_URL,
            FINAL_LATEX_CONTENT: latexResponse.data.data.finalLatexContent,
          },
          {
            responseType: "blob",
            headers: {
              "Content-Type": "application/json",
            },
          }
        );

        // Create blob and URL for viewing
        const blob = new Blob([pdfResponse.data], { type: "application/pdf" });
        const url = window.URL.createObjectURL(blob);

        setPdfUrl(url);
        setMessage("✅ Why I Fit PDF generated successfully!");
      } else {
        setMessage(
          "❌ Error generating document: " + latexResponse.data.message
        );
      }
    } catch (error) {
      console.error("❌ Error generating PDF:", error);
      setMessage(
        "❌ Error generating PDF. Make sure the backend is running on port 8080."
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Download PDF
  const handleDownloadPDF = () => {
    if (pdfUrl) {
      const link = document.createElement("a");
      link.href = pdfUrl;
      link.download = `why-i-fit-${
        editableData.TARGET_COMPANY || "document"
      }.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      setMessage("📄 PDF downloaded successfully!");
    }
  };

  // Print PDF
  const handlePrintPDF = () => {
    if (pdfUrl) {
      const printWindow = window.open(pdfUrl, "_blank");
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  return (
    <div>
      {/* Header - Same as Cover Letter */}
      <header className="wif-header">
        <h1>ResuMe</h1>
        <p>
          <strong>Why I Fit For This Role - Document Generator</strong>
        </p>
        <button
          onClick={() => {
            localStorage.removeItem("loggedIn");
            navigate("/login");
          }}
        >
          🚪 Logout
        </button>
      </header>

      <div className="why-i-fit-container">
        {/* Left Panel - Field Selection */}
        <div className="wif-left-panel">
          <h2>Document Fields</h2>
          <ul className="field-list">
            {Object.keys(fieldLabels).map((field) => (
              <li
                key={field}
                className={selectedField === field ? "active" : ""}
                onClick={() => setSelectedField(field)}
              >
                {fieldLabels[field]}
              </li>
            ))}
            <button
              className="generate-btn"
              onClick={handleGeneratePDF}
              disabled={isLoading}
            >
              {isLoading ? "⏳ Generating..." : "MAKE"}
            </button>
            <button
              className="generate-btn"
              onClick={() => navigate("/jdinput")}
            >
              HOME
            </button>
          </ul>
        </div>

        {/* Middle Panel - Editor */}
        <div className="wif-middle-panel">
          <h2>{fieldLabels[selectedField]}</h2>
          {selectedField === "WHY_I_FIT_CONTENT" ? (
            <textarea
              value={editableData[selectedField] || ""}
              onChange={(e) => handleFieldChange(selectedField, e.target.value)}
              rows={20}
              placeholder="Edit your Why I Fit content here..."
            />
          ) : (
            <input
              type="text"
              value={editableData[selectedField] || ""}
              onChange={(e) => handleFieldChange(selectedField, e.target.value)}
              placeholder={`Enter ${fieldLabels[
                selectedField
              ].toLowerCase()}...`}
            />
          )}

          {message && (
            <div
              className={`status ${
                message.includes("✅")
                  ? "success"
                  : message.includes("❌")
                  ? "error"
                  : "info"
              }`}
            >
              {message}
            </div>
          )}
        </div>

        {/* Right Panel - PDF Preview */}
        <div className="wif-right-panel">
          <h2>Why I Fit Preview</h2>
          {pdfUrl ? (
            <>
              <iframe
                src={pdfUrl}
                title="Why I Fit PDF"
                frameBorder="0"
                width="100%"
                height="100%"
              />
            </>
          ) : (
            <div className="no-preview">
              <p>No document generated yet</p>
              <p className="preview-hint">
                Edit fields and click "MAKE" to generate PDF
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Loading Overlay */}
      {isLoading && (
        <div className="loading-overlay">
          <div className="loading-spinner" />
          <p>Generating your Why I Fit PDF...</p>
        </div>
      )}
    </div>
  );
}

export default WhyIFitGeneration;
