import axios from "axios";

const GEMINI_API_URL =
  "https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent";

export async function callGeminiAPI(prompt) {
  const apiKey = "AIzaSyB1mo0mYLvRkle-6OCLUFpS0rbaSdIHiHI";

  try {
    const response = await axios.post(
      `${GEMINI_API_URL}?key=${apiKey}`,
      {
        contents: [
          {
            role: "user",
            parts: [{ text: prompt }],
          },
        ],
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    const fullText =
      response.data.candidates?.[0]?.content?.parts?.[0]?.text || "";
    console.log("🔁 Full Gemini Response:", fullText);

    // Gemini responded with JSON string, so parse it
    let parsed = {};
    try {
      parsed = JSON.parse(fullText.replace(/```json|```/g, "").trim());
    } catch (err) {
      console.error("❌ JSON parsing failed", err);
    }

    const updatedLatex = parsed.updatedLatex || "";
    const changesSummary = parsed.changesSummary || [];

    console.log("✅ Extracted LaTeX Part:", updatedLatex.slice(0, 300));
    console.log("📝 Extracted Changes Summary:", changesSummary);

    return {
      updatedLatex,
      changesSummary,
    };
  } catch (error) {
    console.error("Gemini API Error:", error.response?.data || error.message);
    alert(
      "Gemini API Error: " +
        JSON.stringify(error.response?.data || error.message)
    );
    return {
      updatedLatex: "Error generating LaTeX.",
      changesSummary: [],
    };
  }
}
