export function sanitizeLatexContent(content) {
  if (!content) return "";

  // 1. Convert Markdown bold with %: **45%** → \textbf{45\%}
  content = content.replace(
    /\*\*(\d+(\.\d+)?)\%?\*\*/g,
    (_, num) => `\\textbf{${num}\\%}`
  );

  // 2. Escape unescaped % (only if not already handled above)
  content = content.replace(/(^|[^\\])%/g, (match, p1) => `${p1}\\%`);

  // 3. Escape unescaped &
  content = content.replace(/(^|[^\\])&/g, (match, p1) => `${p1}\\&`);

  // 4. Make sure non-bolded percentages like 20% → \textbf{20\%}
  content = content.replace(
    /(?<!\\textbf{)(\d+(\.\d+)?)\\%/g,
    (_, num) => `\\textbf{${num}\\%}`
  );

  // 5. Remove bad trailing backslashes
  content = content.replace(/\\\s*$/gm, "");

  return content;
}
export function sanitizeSkillsLatex(rawSkills) {
  return rawSkills
    .split("\n")
    .map((line) => {
      // Replace all `\&` except the first one with plain `&`
      const parts = line.split("\\&");
      if (parts.length === 1) return line.trim(); // no `\&` found

      const first = parts[0].trim();
      const second = parts[1].trim();

      const rest = parts
        .slice(2)
        .map((part) => part.trim())
        .join(" & ");

      // Join everything with proper LaTeX tabular format
      let cleaned = `${first} \\& ${second}`;
      if (rest) cleaned += ` & ${rest}`;
      if (!cleaned.trim().endsWith("\\\\")) cleaned += " \\\\";

      return cleaned;
    })
    .join("\n");
}
