// src/components/ResumeGeneration.js
import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import "../CSS/ResumeGeneration.css";

function ResumeGeneration() {
  const location = useLocation();
  const { resumeData = {}, pdfUrl } = location.state || {};

  const sections = [
    { id: "PROFESSIONAL_SUMMARY", label: "Professional Summary" },
    { id: "TECHNICAL_SKILLS", label: "Technical Skills" },
    { id: "METLIFE_BULLET_POINTS", label: "MetLife Experience" },
    { id: "ADONS_BULLET_POINTS", label: "Adons Experience" },
  ];

  const [selectedSection, setSelectedSection] = useState(sections[0].id);
  const [editableData, setEditableData] = useState({ ...resumeData });

  const handleEditChange = (e) => {
    setEditableData({
      ...editableData,
      [selectedSection]: e.target.value,
    });
  };

  return (
    <div className="resume-layout">
      {/* Left Column: Section list */}
      <div className="left-panel">
        <h2>Resume Sections</h2>
        <ul className="section-list">
          {sections.map((sec) => (
            <li
              key={sec.id}
              className={selectedSection === sec.id ? "active" : ""}
              onClick={() => setSelectedSection(sec.id)}
            >
              {sec.label}
            </li>
          ))}
        </ul>
      </div>

      {/* Middle Column: Editable text area */}
      <div className="middle-panel">
        <h2>Edit Section</h2>
        <textarea
          rows="20"
          value={editableData[selectedSection] || ""}
          onChange={handleEditChange}
          className="edit-textarea"
        />
      </div>

      {/* Right Column: PDF Preview */}
      <div className="right-panel">
        <h2>Live Preview</h2>
        {pdfUrl ? (
          <iframe
            title="Generated Resume"
            src={pdfUrl}
            width="100%"
            height="100%"
            frameBorder="0"
          ></iframe>
        ) : (
          <p className="no-preview">No PDF preview available</p>
        )}
      </div>
    </div>
  );
}

export default ResumeGeneration;
