// ✅ promptBuilder.js
export function buildResumePrompt({ jobDescription, resumeTemplate }) {
  return `
You are an expert resume customization assistant. Given the LaTeX resume below and the job description, update only the placeholders marked with “% JD_...” (e.g., % JD_METLIFE_BULLET_1) to match the job description. Do not change any other parts.

--- Job Description ---
${jobDescription}

--- Full Resume Template (LaTeX) ---
${resumeTemplate.full}

Return only the full updated LaTeX content. Ensure the resume stays under one page and remains LaTeX formatted properly.
`;
}
