// JDInput.js
import React, { useState } from "react";
import {
  buildGeminiPrompt,
  resumeTemplate,
  coverLetterTemplate,
  coldEmailTemplate,
} from "../utils/promptBuilder";
import { callGeminiAPI } from "../utils/apiHandler";

function JDInput({ onJDUpdate }) {
  const [jobDesc, setJobDesc] = useState("");
  const [loading, setLoading] = useState(false);
  const [summaryLatex, setSummaryLatex] = useState("");
  const [skillsLatex, setSkillsLatex] = useState("");
  const [metlifeLatex, setMetlifeLatex] = useState("");
  const [adonsLatex, setAdonsLatex] = useState("");
  const [coverLetter, setCoverLetter] = useState("");
  const [coldEmail, setColdEmail] = useState("");

  const handleGenerate = async () => {
    if (!jobDesc.trim()) return;
    setLoading(true);
    if (onJDUpdate) onJDUpdate(jobDesc);

    const resumePrompt = buildResumePrompt({ jobDescription: jobDesc });
    const coverPrompt = buildCoverLetterPrompt({
      jobDescription: jobDesc,
      resumeSummary: resumeTemplate.full,
    });
    const coldPrompt = buildColdEmailPrompt({
      jobDescription: jobDesc,
      resumeSummary: resumeTemplate.full,
    });

    const [resumeData, coverLetterText, coldEmailText] = await Promise.all([
      callGeminiAPI(resumePrompt),
      callGeminiAPI(coverPrompt),
      callGeminiAPI(coldPrompt),
    ]);

    setSummaryLatex(resumeData.summaryLatex || "");
    setSkillsLatex(resumeData.skillsLatex || "");
    setMetlifeLatex(resumeData.metlifeLatex || "");
    setAdonsLatex(resumeData.adonsLatex || "");
    setCoverLetter(coverLetterText);
    setColdEmail(coldEmailText);
    setLoading(false);
  };

  const renderTextBox = (title, content) => (
    <div style={{ marginBottom: "2rem" }}>
      <h3>{title}</h3>
      <textarea
        rows="10"
        value={content}
        readOnly
        style={{ width: "100%", background: "#f9f9f9" }}
      />
    </div>
  );

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Paste Job Description</h2>
      <textarea
        rows="8"
        cols="100"
        placeholder="Paste job description here..."
        value={jobDesc}
        onChange={(e) => setJobDesc(e.target.value)}
      />
      <br />
      <button onClick={handleGenerate} disabled={loading}>
        {loading ? "Generating..." : "Generate Tailored Content"}
      </button>

      {renderTextBox("üìå Updated Summary Section (LaTeX)", summaryLatex)}
      {renderTextBox(
        "‚öôÔ∏è Updated Technical Skills Section (LaTeX)",
        skillsLatex
      )}
      {renderTextBox("üè¢ MetLife Experience (LaTeX)", metlifeLatex)}
      {renderTextBox("üè¢ Adons Soft Tech Experience (LaTeX)", adonsLatex)}
      {renderTextBox("‚úâÔ∏è Cover Letter", coverLetter)}
      {renderTextBox("üì® Cold Email", coldEmail)}
    </div>
  );
}

export default JDInput;
