// JDInput.js
import React, { useState } from "react";
import { resumeTemplate } from "../utils/latexTemplates";
import { buildResumePrompt } from "../utils/promptBuilder";
import { callGeminiAPI } from "../utils/apiHandler";
import ResumeDiffViewer from "./ResumeDiffViewer";

const downloadLatexFile = (latexCode) => {
  const blob = new Blob([latexCode], { type: "text/plain" });
  const url = URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = url;
  a.download = "updated_resume.tex";
  a.click();
  URL.revokeObjectURL(url);
};

function JDInput({ onJDUpdate }) {
  const [jobDesc, setJobDesc] = useState("");
  const [updatedLatex, setUpdatedLatex] = useState("");
  const [loading, setLoading] = useState(false);
  const [originalLatex, setOriginalLatex] = useState(resumeTemplate.full);
  const [showDiffOnly, setShowDiffOnly] = useState(false);

  const handleCopy = () => {
    navigator.clipboard.writeText(updatedLatex);
    alert("LaTeX copied to clipboard!");
  };

  const handleGenerate = async () => {
    if (!jobDesc.trim()) return;
    setLoading(true);
    if (onJDUpdate) onJDUpdate(jobDesc);

    const prompt = buildResumePrompt({
      jobDescription: jobDesc,
      resumeTemplate: resumeTemplate.full,
    });

    const result = await callGeminiAPI(prompt);
    setUpdatedLatex(result);
    setLoading(false);
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Paste Job Description</h2>
      <textarea
        rows="8"
        cols="100"
        placeholder="Paste job description here..."
        value={jobDesc}
        onChange={(e) => setJobDesc(e.target.value)}
      />
      <br />
      <button onClick={handleGenerate} disabled={loading}>
        {loading ? "Generating..." : "Generate Resume Sections"}
      </button>

      {updatedLatex && (
        <>
          <h2>üîç Compare Resume Sections</h2>
          {originalLatex && updatedLatex && (
            <ResumeDiffViewer original={originalLatex} updated={updatedLatex} />
          )}
          {originalLatex && updatedLatex ? (
            <ResumeDiffViewer original={originalLatex} updated={updatedLatex} />
          ) : (
            <p>No differences to display yet.</p>
          )}

          <div style={{ display: "flex", gap: "1rem", marginTop: "1rem" }}>
            <div style={{ flex: 1 }}>
              <h4>üîí Original Template</h4>
              <textarea
                value={originalLatex}
                readOnly
                rows="25"
                style={{ width: "100%", backgroundColor: "#f0f0f0" }}
              />
            </div>
            <div style={{ flex: 1 }}>
              <h4>‚ú® Updated By Gemini</h4>
              <textarea
                value={updatedLatex}
                readOnly
                rows="25"
                style={{ width: "100%", backgroundColor: "#e8fff0" }}
              />
              <div style={{ marginTop: "0.5rem" }}>
                <button onClick={handleCopy}>Copy LaTeX</button>
                <button
                  onClick={() => downloadLatexFile(updatedLatex)}
                  style={{ marginLeft: "1rem" }}
                >
                  Download .tex
                </button>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default JDInput;
