// JDInput.js
import React, { useState } from "react";
import {
  buildGeminiPrompt,
  resumeTemplate,
  coverLetterTemplate,
  coldEmailTemplate,
} from "../utils/promptBuilder";
import { callGeminiAPI } from "../utils/apiHandler";

function JDInput({ onJDUpdate }) {
  const [jobDesc, setJobDesc] = useState("");
  const [loading, setLoading] = useState(false);
  const [summaryLatex, setSummaryLatex] = useState("");
  const [skillsLatex, setSkillsLatex] = useState("");
  const [metlifeLatex, setMetlifeLatex] = useState("");
  const [adonsLatex, setAdonsLatex] = useState("");
  const [coverLetter, setCoverLetter] = useState("");
  const [coldEmail, setColdEmail] = useState("");

  const handleGenerate = async () => {
    if (!jobDesc.trim()) return;
    setLoading(true);
    if (onJDUpdate) onJDUpdate(jobDesc);

    const prompt = buildGeminiPrompt({
      jobDescription: jobDesc,
      resumeTemplate: resumeTemplate.full,
      coverLetterTemplate,
      coldEmailTemplate,
    });

    const result = await callGeminiAPI(prompt);

    setSummaryLatex(result.summaryLatex || "");
    setSkillsLatex(result.skillsLatex || "");
    setMetlifeLatex(result.metlifeLatex || "");
    setAdonsLatex(result.adonsLatex || "");
    setCoverLetter(result.coverLetter || "");
    setColdEmail(result.coldEmail || "");
    setLoading(false);
  };

  const renderTextBox = (title, content) => (
    <div className="bg-white rounded-md shadow-md p-4 mb-6">
      <h3 className="text-lg font-semibold text-gray-800 mb-2">{title}</h3>
      <textarea
        rows="10"
        value={content}
        readOnly
        className="w-full p-3 border border-gray-300 rounded-md bg-gray-50 font-mono text-sm"
      />
    </div>
  );

  return (
    <div className="max-w-5xl mx-auto px-6 py-8 bg-gray-50 min-h-screen">
      <h2 className="text-3xl font-bold text-indigo-700 mb-6">
        üéØ Job Description to Resume Tailoring Assistant
      </h2>

      <div className="mb-6">
        <label className="block text-lg font-medium text-gray-700 mb-2">
          üìÑ Paste Job Description
        </label>
        <textarea
          rows="8"
          placeholder="Paste job description here..."
          value={jobDesc}
          onChange={(e) => setJobDesc(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-md shadow-sm focus:ring-2 focus:ring-indigo-400"
        />
      </div>

      <button
        onClick={handleGenerate}
        disabled={loading}
        className="bg-indigo-600 hover:bg-indigo-700 text-white font-medium px-6 py-2 rounded-md transition duration-200 disabled:opacity-50"
      >
        üöÄ {loading ? "Generating..." : "Generate Tailored Content"}
      </button>

      <div className="mt-10">
        {renderTextBox("üìå Updated Summary Section (LaTeX)", summaryLatex)}
        {renderTextBox(
          "‚öôÔ∏è Updated Technical Skills Section (LaTeX)",
          skillsLatex
        )}
        {renderTextBox("üè¢ MetLife Experience (LaTeX)", metlifeLatex)}
        {renderTextBox("üè¢ Adons Soft Tech Experience (LaTeX)", adonsLatex)}
        {renderTextBox("‚úâÔ∏è Cover Letter", coverLetter)}
        {renderTextBox("üì® Cold Email", coldEmail)}
      </div>
    </div>
  );
}

export default JDInput;
