// src/components/ResumeGeneration.js
import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../CSS/ResumeGeneration.css"; // We'll create this CSS file

function ResumeGeneration() {
  const location = useLocation();
  const navigate = useNavigate();

  // Get the data passed from JDInput
  const { resumeData, jobDescription, companyName } = location.state || {};

  // State for PDF generation
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [pdfUrl, setPdfUrl] = useState(null);
  const [showPDFViewer, setShowPDFViewer] = useState(false);

  // Check if we have the required data
  useEffect(() => {
    if (!resumeData) {
      setMessage("‚ùå No resume data found. Please generate content first.");
    } else {
      setMessage("‚úÖ Resume data loaded successfully! Ready to generate PDF.");
    }
  }, [resumeData]);

  // Generate PDF using our Spring Boot backend
  const handleGeneratePDF = async () => {
    if (!resumeData) {
      setMessage("‚ùå No resume data available");
      return;
    }

    setIsLoading(true);
    setMessage("");

    try {
      console.log("üöÄ Sending resume data to backend:", resumeData);

      const response = await axios.post(
        "http://localhost:8080/api/resume/generate",
        resumeData,
        {
          responseType: "blob",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      // Create blob and URL for viewing
      const blob = new Blob([response.data], { type: "application/pdf" });
      const url = window.URL.createObjectURL(blob);

      setPdfUrl(url);
      setShowPDFViewer(true);

      setMessage("‚úÖ Resume PDF generated successfully!");
    } catch (error) {
      console.error("‚ùå Error generating PDF:", error);
      setMessage(
        "‚ùå Error generating PDF. Make sure the backend is running on port 8080."
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Download PDF
  const handleDownloadPDF = () => {
    if (pdfUrl) {
      const link = document.createElement("a");
      link.href = pdfUrl;
      link.download = `${companyName || "resume"}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      setMessage("üìÑ PDF downloaded successfully!");
    }
  };

  // Print PDF
  const handlePrintPDF = () => {
    if (pdfUrl) {
      const printWindow = window.open(pdfUrl, "_blank");
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  // Go back to JDInput
  const handleGoBack = () => {
    navigate("/jdinput");
  };

  return (
    <div className="resume-generation-container">
      {/* Header */}
      <header className="resume-header">
        <h1>üìÑ Resume Generation</h1>
        <p>Generate professional PDF resume from AI-tailored content</p>
        <button onClick={handleGoBack} className="back-btn">
          ‚Üê Back to Job Analysis
        </button>
      </header>

      <div className="main-content">
        {/* Left Panel - Data Preview */}
        <div className="data-panel">
          <h2>üìã Generated Content Preview</h2>

          {companyName && (
            <div className="info-box">
              <h3>üè¢ Target Company</h3>
              <p>{companyName}</p>
            </div>
          )}

          {resumeData ? (
            <>
              <div className="content-section">
                <h3>üìù Professional Summary</h3>
                <div className="latex-preview">
                  {resumeData.PROFESSIONAL_SUMMARY || "No content generated"}
                </div>
              </div>

              <div className="content-section">
                <h3>üõ†Ô∏è Technical Skills</h3>
                <div className="latex-preview">
                  {resumeData.TECHNICAL_SKILLS || "No content generated"}
                </div>
              </div>

              <div className="content-section">
                <h3>üè¢ MetLife Experience</h3>
                <div className="latex-preview">
                  {resumeData.METLIFE_BULLET_POINTS || "No content generated"}
                </div>
              </div>

              <div className="content-section">
                <h3>üè≠ Adons Experience</h3>
                <div className="latex-preview">
                  {resumeData.ADONS_BULLET_POINTS || "No content generated"}
                </div>
              </div>
            </>
          ) : (
            <div className="no-data">
              <p>
                ‚ùå No resume data available. Please go back and generate content
                first.
              </p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="action-buttons">
            <button
              onClick={handleGeneratePDF}
              disabled={!resumeData || isLoading}
              className="generate-btn"
            >
              {isLoading ? "‚è≥ Generating..." : "üéØ Generate PDF Resume"}
            </button>
          </div>
        </div>

        {/* Right Panel - PDF Viewer */}
        {showPDFViewer && (
          <div className="pdf-panel">
            <div className="pdf-header">
              <h2>üìÑ Generated Resume</h2>
              <div className="pdf-actions">
                <button
                  onClick={handleDownloadPDF}
                  className="action-btn download"
                >
                  üíæ Download
                </button>
                <button onClick={handlePrintPDF} className="action-btn print">
                  üñ®Ô∏è Print
                </button>
                <button
                  onClick={() => setShowPDFViewer(false)}
                  className="action-btn close"
                >
                  ‚úï Close
                </button>
              </div>
            </div>
            <div className="pdf-viewer">
              <iframe
                src={pdfUrl}
                width="100%"
                height="100%"
                title="Generated Resume"
                frameBorder="0"
              />
            </div>
          </div>
        )}
      </div>

      {/* Status Message */}
      {message && (
        <div
          className={`status-message ${
            message.includes("‚úÖ") ? "success" : "error"
          }`}
        >
          {message}
        </div>
      )}

      {/* Loading Overlay */}
      {isLoading && (
        <div className="loading-overlay">
          <div className="loading-content">
            <div className="spinner"></div>
            <p>Generating your professional resume... Please wait</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default ResumeGeneration;
